import React, { useState } from "react";
import { Paper, Divider, Typography, Grid } from "@material-ui/core";
import { makeStyles, MuiThemeProvider } from "@material-ui/core/styles";
import { storiesOf } from "@storybook/react";
import themes from "../../Theme/themes";
import PaxPackage from "../PaxPackage";
import { styles } from "../styles";

const useStyles = makeStyles((theme) => ({
    ...styles,
    rootBg: {
        background: theme.palette.primary.main,
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
}));

const PaxPackageStories = ({
    mode, brand, isMobile, error, errorMsg, disabledExe, disabledEco
}) => {
    const classes = useStyles(themes[brand]);
    const initialState = {
        input: [{ value: null, adults: 1, children: 0, ages: [] }],
        classes: "eco",
        directFlight: false,
    };
    const [open, setOpen] = useState(false);
    const [fields, setFields] = useState(initialState);
    const [valueTextField, setTextField] = useState({ value: "" });

    const handleOpen = () => {
        setOpen(!open);
    };

    const isValid = () => {
        let boolValid = true;
        setFields({
            ...fields,
            input: fields.input.map((field) => ({
                ...field,
                ages: field.ages.map((age) => {
                    const validated = age.value === 0;
                    boolValid = boolValid && !validated;
                    return {
                        ...age,
                        isValid: validated,
                    };
                }),
            })),
        });
        return boolValid;
    };

    const handleChange = (value) => {
        if (isValid()) {
            setTextField({ value });
            handleOpen();
        }
    };

    const handleAdd = () => {
        const values = { ...fields };
        values.input.push(initialState.input[0]);
        setFields(values);
    };

    const handleRemove = (i) => {
        const values = { ...fields };
        values.input.splice(i, 1);
        setFields(values);
    };

    const handleRemoveRoom = () => {
        const values = { ...fields };
        values.input.pop();
        setFields(values);
    };

    const handleAdults = (idx, adult) => {
        const adults = { ...fields };
        adults.input[idx].adults = adult;
        setFields(adults);
    };

    const handleAddChildren = (fieldIdx, child) => {
        const children = { ...fields };
        children.input[fieldIdx].children = child;
        children.input[fieldIdx].ages.push({ value: 0 });
        setFields(children);
    };
    const handleRemoveChildren = (fieldIdx, child) => {
        const children = { ...fields };
        children.input[fieldIdx].children = child;
        children.input[fieldIdx].ages.pop();
        setFields(children);
    };

    const handleChildAge = (fieldIdx, ageIdx, e) => {
        const childAge = { ...fields };
        childAge.input[fieldIdx].ages[ageIdx].value = e.target.value;
        setFields(childAge);
        isValid();
    };
    const handleClear = () => {
        setFields(initialState);
        setTextField({ value: "" });
    };

    const handleClasses = (field) => {
        const values = { ...fields };
        values.classes = field;
        setFields(values);
    };
    const handleDirectFlight = () => {
        const values = { ...fields };
        values.directFlight = !values.directFlight;
        setFields(values);
    };

    return (
        <MuiThemeProvider theme={themes[brand]}>
            <Paper className={classes[mode]} style={{ background: "#237FD0" }}>
                <Typography variant="h3">Pax Package</Typography>
                <br />
                <Divider />
                <br />
                <Grid container direction="row" justify="flex-start" spacing={2}>
                    <Grid item xs={12}>
                        <PaxPackage
                            initialState={initialState}
                            open={open}
                            fields={fields}
                            onClick={handleOpen}
                            handleAdd={handleAdd}
                            handleRemove={handleRemove}
                            handleRemoveRoom={handleRemoveRoom}
                            handleAdults={handleAdults}
                            handleAddChildren={handleAddChildren}
                            handleRemoveChildren={handleRemoveChildren}
                            handleChildAge={handleChildAge}
                            handleChange={handleChange}
                            valueTextField={valueTextField.value}
                            handleClear={handleClear}
                            handleClasses={handleClasses}
                            handleDirectFlight={handleDirectFlight}
                            error={error}
                            errorMsg={errorMsg}
                            isMobile={isMobile}
                            disabledExe={disabledExe}
