import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { withStyles } from "@material-ui/core/styles";
import Plus from "@cvccorp-components/icons/dist/Plus";
import Minus from "@cvccorp-components/icons/dist/Minus";
import User from "@cvccorp-components/icons/dist/User";
import Delete from "@cvccorp-components/icons/dist/Delete";
import ArrowLeft from "@cvccorp-components/icons/dist/ArrowLeft";

//  COMPONENTS
import Select from "../../Select";
import TextField from "../../TextField";
import Typography from "../../Typography";
import Button from "../../Button";
import Box from "../../Box";
import Overlay from "../../Overlay";
import Animate from "../../Animate";

//  STYLES
import { styles } from "../styles";

//  OPTIONS
import { childAgeOptions } from "./options";

const uuidv4 = require("uuid/v4");

const Hotel = ({
    classes,
    error,
    errorMsg,
    open,
    fields,
    onClick,
    handleClose,
    handleChange,
    handleAdd,
    handleRemove,
    handleAdults,
    handleRemoveRoom,
    handleChildAge,
    handleRemoveChildren,
    handleAddChildren,
    handleClear,
    initialState,
    isMobile,
    height,
}) => {
    const guests = fields
        .map(field => field.adults + field.children)
        .reduce((accumulator, item) => accumulator + item, 0);

    const countRoom =
        fields.length > 1 ? `${fields.length} Quartos` : `${fields.length} Quarto`;
    const countGuests = guests > 1 ? `${guests} Hóspedes` : `${guests} Hóspede`;
    const guestsTextValue = `${countRoom}  -  ${countGuests}`;
    const classNameOpen = clsx(classes.root, classes.closed);

    const [loaded, setLoaded] = useState(false);
    const [disabledLimparButton, setDisabledLimparButton] = useState();

    useEffect(() => {
        setLoaded(true);
    }, []);

    useEffect(() => {
        const disabled = JSON.stringify(initialState) === JSON.stringify(fields);
        setDisabledLimparButton(disabled);
    }, [fields]);

    const [isOpenAnimation, setOpenAnimation] = useState(false);

    const onOpenAnimate = () => {
        setOpenAnimation(true);
    };
    const onCloseAnimate = () => {
        setOpenAnimation(false);
    };

    return (
        <>
            {loaded && (
                <TextField
                    value={guestsTextValue}
                    icon={<User />}
                    size="large"
                    transparent
                    disabled
                    error={error}
                    errorMsg={errorMsg}
                    onClick={onClick}
                    className={classes.customTextPax}
                    inputClassName={classes.input}
                    labelClassName={classes.input}
                    noBorder
                />
            )}
            <Animate isOpen={open} onOpen={onOpenAnimate} onClose={onCloseAnimate}>
                <Overlay onClick={handleClose} />
                <Box className={!isOpenAnimation ? classNameOpen : classes.root}>
                    <div className={classes.container}>
                        {!isMobile ? (
                            <h1 className={classes.title}>
                                <Typography type="body1regular">
                                    <User className={classes.icon} size="medium" />
                                    {guestsTextValue}
                                </Typography>
                            </h1>
                        ) : (
                            <h1 className={classes.title}>
                                <Typography type="body1regular" onClick={handleClose}>
                                    <ArrowLeft className={classes.icon} size="medium" />
                  Selecione os hóspedes
                                </Typography>
                            </h1>
                        )}
                        <div>
                            <div className={classes.section} style={{ maxHeight: height }}>
                                <ul className={classes.list}>
                                    <li className={classes.listItem}>
                                        <span className={classes.content}>
                                            <Typography type="headline5">Quartos</Typography>
                                            <Typography type="subtitle" className={classes.subtitle}>
                                                Adicione os quartos
                                            </Typography>
                                        </span>
                                        <div className={classes.btns}>
                                            <Button
                                                className="btn-remove-quarto"
                                                color="primary"
                                                icon={<Minus />}
                                                size="small"
                                                addRemove
                                                variant="outlined"
                                                iconPosition="center"
                                                disabled={fields.length <= 1}
                                                onClick={() => handleRemoveRoom()}
                                            />
                                            <Typography type="subtitle" className={classes.btnLabel}>
                                                {fields.length}
                                            </Typography>
                                            <Button
                                                className="btn-add-quarto"
                                                color="primary"
                                                icon={<Plus />}
                                                size="small"
                                                addRemove
                                                variant="outlined"
                                                iconPosition="center"
                                                onClick={() => handleAdd()}
                                            />
                                        </div>
                                    </li>
                                    {fields.map((field, fieldIdx) => (
                                        <li key={uuidv4()} className={`${classes.listItem} aut-room-box-${fieldIdx}`}>
                                            <ul className={classes.list}>
                                                <h2 className={clsx(classes.listTitle, `Hotel-room-index-${fieldIdx}`)}>
                                                    <Typography
                                                        type="captionsemibold"
                                                        className={classes.subtitle}
                                                    >
                                                        Quarto {fieldIdx + 1}
                                                    </Typography>
                                                    {fieldIdx > 0 && (
                                                        <button
                                                            type="button"
                                                            className={`${classes.removeRoom} aut-hotel-remove-room-btn`}
                                                            onClick={() => handleRemove(fieldIdx)}
                                                        >
                                                            <Typography
                                                                type="captionsemibold"
                                                                className={classes.removeRoomText}
                                                            >
                                                                <Delete /> Remover
                                                            </Typography>
                                                        </button>
                                                    )}
                                                </h2>
                                                <li className={classes.listItem}>
                                                    <span className={classes.content}>
                                                        <Typography type="headline5">Adultos</Typography>
                                                        <Typography
                                                            type="subtitle"
                                                            className={classes.subtitle}
                                                        >
                                                            Acima de 18 anos
                                                        </Typography>
                                                    </span>
                                                    <div className={classes.btns}>
                                                        <Button
                                                            className={`btn-remove-adulto btn-remove-adulto-${fieldIdx}` }
                                                            color="primary"
                                                            icon={<Minus />}
                                                            size="small"
                                                            addRemove
                                                            variant="outlined"
                                                            iconPosition="center"
                                                            disabled={field.adults <= 1}
                                                            onClick={() =>
                                                                handleAdults(fieldIdx, field.adults - 1)
                                                            }
                                                        />
                                                        <Typography
                                                            type="subtitle"
                                                            className={classes.btnLabel}
                                                        >
                                                            {field.adults}
                                                        </Typography>
                                                        <Button
                                                            className={`btn-add-adulto btn-add-adulto-${fieldIdx}` }
                                                            color="primary"
                                                            icon={<Plus />}
                                                            size="small"
                                                            addRemove
                                                            variant="outlined"
                                                            iconPosition="center"
                                                            onClick={() =>
                                                                handleAdults(fieldIdx, field.adults + 1)
                                                            }
                                                        />
                                                    </div>
                                                </li>
                                                <li className={classes.listItem}>
                                                    <span className={classes.content}>
                                                        <Typography type="headline5">Crianças</Typography>
                                                        <Typography
                                                            type="subtitle"
                                                            className={classes.subtitle}
                                                        >
                                                            De 0 até 17 anos
                                                        </Typography>
                                                    </span>
                                                    <div className={classes.btns}>
                                                        <Button
                                                            className={`btn-remove-crianca btn-remove-crianca-${fieldIdx}` }
                                                            color="primary"
                                                            icon={<Minus />}
                                                            size="small"
                                                            addRemove
                                                            variant="outlined"
                                                            iconPosition="center"
                                                            disabled={field.children <= 0}
                                                            onClick={() =>
                                                                handleRemoveChildren(
                                                                    fieldIdx,
                                                                    field.children - 1
                                                                )
                                                            }
                                                        />
                                                        <Typography
                                                            type="subtitle"
                                                            className={classes.btnLabel}
                                                        >
                                                            {field.children}
                                                        </Typography>
                                                        <Button
                                                            className={`btn-add-crianca btn-add-crianca-${fieldIdx}` }
                                                            color="primary"
                                                            icon={<Plus />}
                                                            size="small"
                                                            addRemove
                                                            variant="outlined"
                                                            iconPosition="center"
                                                            onClick={() =>
                                                                handleAddChildren(fieldIdx, field.children + 1)
                                                            }
                                                        />
                                                    </div>
                                                </li>
                                                {field.children > 0 && (
                                                    <li className={classes.liContainer}>
                                                        {field.ages.map((age, ageIdx) => (
                                                            <div key={uuidv4()} className={classes.body}>
                                                                <span className={classes.content}>
                                                                    <Typography type="headline5">
                                                                        {`Idade da criança ${ageIdx + 1}`}
                                                                    </Typography>
                                                                    <Typography type="body2regular">
                                                                        ao iniciar a viagem
                                                                    </Typography>
                                                                </span>
                                                                <Select
                                                                    customClass={ `idade-quarto-${fieldIdx}-age-${ageIdx}`}
                                                                    className={classes.ageSelect}
                                                                    size="small"
                                                                    label="Idade"
                                                                    placeholder="Idade"
                                                                    select={childAgeOptions}
                                                                    onChange={e =>
                                                                        handleChildAge(fieldIdx, ageIdx, e)
                                                                    }
                                                                    value={age.value}
                                                                />
                                                            </div>
                                                        ))}
                                                    </li>
                                                )}
                                            </ul>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                            {!isMobile ? (
                                <div className={classes.footer}>
                                    <Button
                                        className={classes.btn}
                                        color="primary"
                                        label="Limpar"
                                        variant="text"
                                        disabled={disabledLimparButton}
                                        onClick={handleClear}
                                    />
                                    <Button
                                        className={classes.btnApply}
                                        color="primary"
                                        label="Aplicar"
                                        variant="text"
                                        onClick={() => handleChange(guestsTextValue)}
                                    />
                                </div>
                            ) : (
                                <div
                                    className={
                                        open
                                            ? classes.footer
                                            : clsx(classes.footer, classes.footerClose)
                                    }
                                >
                                    <Button
                                        color="secondary"
                                        label={`Aplicar ${guestsTextValue}`}
                                        variant="contained"
                                        onClick={() => handleChange(guestsTextValue)}
                                        fullWidth
                                    />
                                </div>
                            )}
                        </div>
                    </div>
                </Box>
            </Animate>
        </>
    );
};

Hotel.defaultProps = {
    error: false,
    isMobile: false,
    open: false,
    onClick: undefined,
    handleClose: undefined,
    handleAdd: undefined,
    handleRemove: undefined,
    handleRemoveRoom: undefined,
    handleAdults: undefined,
    handleAddChildren: undefined,
    handleRemoveChildren: undefined,
    handleChildAge: undefined,
    handleChange: undefined,
    handleClear: undefined,
    errorMsg: "",
    height: "auto",
};

Hotel.propTypes = {
    error: PropTypes.bool,
    isMobile: PropTypes.bool,
    initialState: PropTypes.arrayOf(PropTypes.object).isRequired,
    open: PropTypes.bool,
    fields: PropTypes.arrayOf(PropTypes.object).isRequired,
    onClick: PropTypes.func,
    handleClose: PropTypes.func,
    handleAdd: PropTypes.func,
    handleRemove: PropTypes.func,
    handleRemoveRoom: PropTypes.func,
    handleAdults: PropTypes.func,
    handleAddChildren: PropTypes.func,
    handleRemoveChildren: PropTypes.func,
    handleChildAge: PropTypes.func,
    handleChange: PropTypes.func,
    handleClear: PropTypes.func,
    errorMsg: PropTypes.string,
    height: PropTypes.string,
};

export default withStyles(styles, { withTheme: true })(Hotel);
