import React from "react";
import { withStyles } from "@material-ui/core/styles";
import PropTypes from "prop-types";
import Filter from "../Filter";
import FilterChip from "../FilterChip";
import { styles } from "./styles";

const FilterGrid = ({
    filter,
    classes,
    onApply,
    onReset,
    label,
    isAmenities,
    isLuggage,
    noModal,
    onChange,
    items,
    isMobile,
    open,
    onOpen,
    onClose,
    sufix,
    onActive,
    className,
    height,
}) => {
    const getSelectedValues = () => {
        const selecteds = items.filter(v => v.checked === true);
        return selecteds;
    };

    const selecteds = getSelectedValues();

    const isActive = !!selecteds.length;

    const change = (option, index, isChecked) => {
        Object.assign(option, { checked: isChecked });
        Object.assign(items[index], { checked: isChecked });
        onChange(option, items);
        onActive(getSelectedValues().length > 0);
    };

    const cleanFilterOnClose = () => {
        if (!filter.applied) {
            const newItems = items
                .filter(el => el.checked)
                .map(el => {
                    el.checked = false;
                    return el;
                });
            if (onChange) onChange(items, newItems);
            if (onActive) onActive(false);
        }
    };

    return (
        <Filter
            label={label}
            className={className}
            type="grid"
            noModal={noModal}
            isMobile={isMobile}
            height={height}
            items={items.map((option, index) => (
                <FilterChip
                    className={classes.chip}
                    key={option}
                    disabled={option.disabled}
                    isActive={option.checked}
                    isAmenities={isAmenities}
                    isLuggage={isLuggage}
                    label={
                        <span>
                            {option.icon && <option.icon size={option.size} />}
                            <span className={classes.amenitiesText}>{option.label}</span>
                        </span>
                    }
                    onClick={isChecked => change(option, index, isChecked)}
                />
            ))}
            sufix={sufix}
            onOpen={() => onOpen(true)}
            onClose={() => {
                cleanFilterOnClose();
                onClose();
            }}
            isOpen={open}
            onReset={() => {
                onReset();
            }}
            onApply={() => {
                onApply();
            }}
            disabled={selecteds.length === 0}
            isActive={!noModal && isActive}
            value={selecteds.length === 1 ? [selecteds[0].label] : selecteds}
        />
    );
};

FilterGrid.defaultProps = {
    isAmenities: false,
    isLuggage: false,
    open: false,
    isMobile: false,
    sufix: "",
    label: "",
    noModal: false,
    items: [],
    onOpen: () => {},
    onClose: () => {},
    onApply: () => {},
    onReset: () => {},
    onChange: () => {},
    onActive: () => {},
};

FilterGrid.propTypes = {
    isAmenities: PropTypes.bool,
    isLuggage: PropTypes.bool,
    isMobile: PropTypes.bool,
    noModal: PropTypes.bool,
    open: PropTypes.bool,
    label: PropTypes.string,
    onOpen: PropTypes.func,
    onClose: PropTypes.func,
    onApply: PropTypes.func,
    onReset: PropTypes.func,
    onChange: PropTypes.func,
    onActive: PropTypes.func,
    sufix: PropTypes.string,
    items: PropTypes.arrayOf(
        PropTypes.shape({
            code: PropTypes.number,
            label: PropTypes.string,
            icon: PropTypes.elementType,
            size: PropTypes.string,
            checked: PropTypes.bool,
        })
    ),
};

export default withStyles(styles, { withTheme: true })(FilterGrid);
