export const styles = theme => ({
    root: {
        overflow: "hidden",
        position: "relative",
    },
    backdrop: {
        background: "#000000b3",
        height: "100%",
        position: "absolute",
        top: 0,
        width: "100%",
        zIndex: 1,
    },
    img: { position: "relative" },
    content: {
        left: "50%",
        position: "absolute",
        top: "50%",
        transform: "translate(-50%, -50%)",
        zIndex: 2,
    },
});
