export const styles = theme => ({
    root: {
        // maxHeight: "1000px",
        overflow: "hidden",
        padding: 0,
        transition: "max-height ease-in-out .5s",
        width: "100%",
        zIndex: theme.level.modal,
        [theme.breakpoints.between("md", "xl")]: {
            borderRadius: "8px",
            top: "9px",
            width: "328px",
            position: "absolute",
        },
        [theme.breakpoints.between("xs", "md")]: {
            borderRadius: 0,
            bottom: 0,
            height: "100%",
            left: 0,
            overflow: "auto",
            position: "fixed",
            right: 0,
            top: 0,
        },
    },
    closed: { maxHeight: 0 },
    container: { padding: `0 ${theme.spacing(2)}px`, cursor: "default" },
    title: {
        borderBottom: `1px solid ${theme.palette.grey[50]}`,
        boxSizing: "border-box",
        display: "flex",
        fontWeight: "normal",
        lineHeight: 1,
        margin: `0 0 ${theme.spacing(2)}px`,
        padding: theme.spacing(2),
        width: `calc(100% - ${theme.spacing(4)}px)`,
    },
    icon: { marginRight: theme.spacing(1) },
    iconUser: {
        marginTop: theme.spacing(-0.625),
        marginRight: theme.spacing(1),
    },
    subtitle: { color: theme.palette.text.secondary },
    subtitlePax: { color: theme.palette.text.primary },
    section: {
        overflow: "hidden auto",
        padding: "1px 10px 1px 0",
        "& + &": {
            borderTop: `1px solid ${theme.palette.grey[50]}`,
            marginTop: theme.spacing(3),
            paddingTop: theme.spacing(3),
        },
        [theme.breakpoints.down("xs")]: {
            marginBottom: "100px"
        }
    },
    sectionTitle: {
        display: "block",
        marginBottom: theme.spacing(3),
    },
    paxTitle: {
        margin: `${theme.spacing(-1.625)}px 0 ${theme.spacing(1.625)}px 0`,
    },
    list: {
        margin: 0,
        padding: 0,
        width: "100%",
    },
    listItem: {
        display: "flex",
        listStyle: "none",
        justifyContent: "space-between",
        alignItems: "center",
        "& + &": { marginTop: theme.spacing(2) },
    },
    listItemAge: {
        alignItems: "flex-start",
        borderTop: `1px solid ${theme.palette.text.disabled}`,
        display: "flex",
        listStyle: "none",
        justifyContent: "space-between",
        marginTop: theme.spacing(3),
        paddingTop: theme.spacing(3),
        flexDirection: "column",
    },
    liContainer: { listStyle: "none" },
    content: {
        display: "flex",
        flexDirection: "column",
        minWidth: "calc(50% + 10px)",
        paddingRight: theme.spacing(2),
    },
    body: {
        marginTop: theme.spacing(3),
        display: "flex",
        justifyContent: "space-between",
        width: "100%",
    },
    flightClass: {
        margin: `0 ${theme.spacing(0.5)}px ${theme.spacing(3)}px 0`,
        width: `calc(50% - ${theme.spacing(0.5)}px)`,
        padding: 0,
        "& + &": {
            margin: `0 0 ${theme.spacing(3)}px ${theme.spacing(0.5)}px`,
        },
    },
    footer: {
        display: "flex",
        justifyContent: "space-between",
        marginTop: theme.spacing(2),
        marginBottom: theme.spacing(2),
        width: "100%",
        [theme.breakpoints.down("xs")]: {
            bottom: theme.spacing(2),
            opacity: 1,
            position: "fixed",
            transition: "all ease-in-out .5s",
            width: `calc(100% - ${theme.spacing(4)}px)`,
            left: theme.spacing(2)
        },
    },
    footerClose: {
        [theme.breakpoints.between("xs", "md")]: {
            transition: "all ease-in-out .5s",
            opacity: 0,
            position: "fixed",
        },
    },
    btnLabel: {
        fontSize: "20px",
        padding: `0 ${theme.spacing(2)}px`,
        verticalAlign: "middle",
    },
    btn: {
        height: "auto",
        minWidth: 0,
        padding: 0,
    },
    btnApply: {
        color: theme.palette.primary.main,
        height: "auto",
        minWidth: 0,
        padding: 0,
    },
    listTitle: {
        alignItems: "center",
        borderTop: `1px solid ${theme.palette.grey[50]}`,
        display: "flex",
        justifyContent: "space-between",
        margin: `0 0 ${theme.spacing(2)}px`,
        paddingTop: theme.spacing(3),
        color: theme.palette.text.primary,
    },
    removeRoom: {
        background: "none",
        border: "none",
        cursor: "pointer",
    },
    removeRoomText: { color: theme.palette.error.main },
    margin: { margin: theme.spacing(1) },
    ageSelect: { width: "40%" },
    ageContainer: {
        width: "110px",
    },
    select: {
        display: "flex",
        flex: 1,
    },
    customTextPax: {
        "& > label": {
            transform: "translate(58px, 20px) scale(1)!important",
        },
    },
    input: { fontSize: "16px" },
    optionsFlight: { marginTop: theme.spacing(2) },
    spaceButton: {
        [theme.breakpoints.between("xs", "md")]: {
            marginBottom: "80px",
        },
    },
});
