import { makeStyles } from "@material-ui/core/styles";

interface stylesProps {
    error?: string,
}

const useStyles = makeStyles((theme) => ({
    root: {
        width: "100%",
    },

    input: {
        ["& fieldset"]: {
            borderRadius: 10,
        },
        width: "100%",
    },
    adornedEnd: {

    },

    outlinedInput: {

    },
    isFocused: {
        "&$isFocused $notchedOutline": {
            borderWidth: "1px",
            borderColor: "#237FD0 !important"
        },
        "&$isFocused $inputVisibilityIcon": {
            color: "#237FD0!important"
        }
    },
    notchedOutline: (props: stylesProps) => ({
        borderWidth: "1px",
        borderColor: props.error ? "#FF3344" : "#CACACA !important",
    }),
    inputVisibilityIcon: {
        cursor: "pointer",
        color: "#CACACA"
    },




}));
export default useStyles;
