import React from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Grid from "@material-ui/core/Grid";
import Typography from "../../Typography";
import Button from "../../Button";
import Price from "../../Price";
import { styles } from "./styles";

const RoomPrice = ({
    classes,
    product,
    totalPrice,
    oldPartialPrice,
    partialPrice,
    pax,
    days,
    onClick,
    taxesIncluded,
    parcel,
    label,
    variant,
    classButton,
    totalPeople,
    symbol,
}) => {
    return (
        <>
            <Grid container>
                <Grid item xs={6} className={classes.btn}>
                    <Price
                        className={classes.priceContainer}
                        product={product}
                        totalPrice={totalPrice}
                        oldPartialPrice={oldPartialPrice}
                        partialPrice={partialPrice}
                        totalPeople={totalPeople}
                        totalDays={days}
                        symbol={symbol}
                    />
                </Grid>
                <Grid item container xs={6} alignItems="center">
                    <Button
                        className={classButton}
                        color="primary"
                        label={label}
                        variant={variant}
                        fullWidth
                        onClick={onClick}
                    />
                </Grid>
            </Grid>
            <Grid item xs={12} className={classes.footer}>
                <Typography type="captionregular">
                    {taxesIncluded ? "Taxas inclusas |" : "Taxas não inclusas |"}
                </Typography>{" "}
                <Typography type="captionsemibold">Em até {parcel}x</Typography>
            </Grid>
        </>
    );
};

RoomPrice.propTypes = {
    classes: PropTypes.object.isRequired,
    onClick: PropTypes.func.isRequired,
    product: PropTypes.string.isRequired,
    pax: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
    days: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
    totalPrice: PropTypes.oneOfType([PropTypes.number, PropTypes.string])
        .isRequired,
    partialPrice: PropTypes.oneOfType([PropTypes.number, PropTypes.string])
        .isRequired,
    taxesIncluded: PropTypes.bool,
    parcel: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
};

export default withStyles(styles, { withTheme: true })(RoomPrice);
