export const styles = theme => (
    {
        coronaWarning: {
            left: "0px",
            right: "0px",
            top: "0",
            height: "130px",
            paddingLeft: "22px",
            paddingRight: "22px",
            background: "#f8db1c",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            fontFamily: "HindMRegular",
            fontStyle: "normal",
            fontWeight: "normal",
            fontSize: "12px",
            lineHeight: "22px",
            transition: "0.4s ease-in-out",
            zIndex: "9999",
            [theme.breakpoints.up("xl")]: {
                height: "60px",
                paddingLeft: "49px",
                paddingRight: "49px",
                fontSize: "16px",
            },
            "& p": {
                marginLeft: "40px",
                width: "80%",
                [theme.breakpoints.up("xl")]: {
                    marginLeft: "0px",
                    width: "auto",
                },
            },
            "& span": {
                fontFamily: "HindMBold",
                [theme.breakpoints.up("xl")]: {
                    marginRight: "11px"
                },
            },
            "& a": {
                marginLeft: "40px",
                fontFamily: "HindMBold",
                color: "#000",
                cursor: "pointer",
                position: "relative",
                marginBottom: "16px",
                [theme.breakpoints.up("xl")]: {
                    marginLeft: "25px",
                    marginRight: "25px",
                    marginBottom: "0",
                },
                "& img": {
                    position: "absolute",
                    top: "5px",
                    paddingLeft: "3px",
                }
            },
            "&.closing": {
                // TO-DO: CHECAR ESSA REGRA!!!!!!!!!!!!!!!!!!!!!!!!!!!
                [theme.breakpoints.down("xl")]: {
                    top: "-500px"
                },
                [theme.breakpoints.up("xl")]: {
                    top: "-150px"
                }
            },
            "& .alertSvg": {
                position: "absolute",
                width: "38px",
                height: "38px",
                left: "11px",
                top: "17px",
                [theme.breakpoints.up("xl")]: {
                    position: "initial",
                    width: "32px",
                    height: "32px",
                    marginRight: "11px",
                }
            },
        },
        coronaFlex: {
            height: "100%",
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            [theme.breakpoints.up("xl")]: {
                width: "100%",
                maxWidth: "1280px",
                paddingLeft: "25px",
                margin: "auto",
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "initial",
            },
            "& .hide-desk": {
                display: "block",
                [theme.breakpoints.up("xl")]: {
                    display: "none"
                }
            },
        },
        coronaClose: {
            cursor: "pointer",
            position: "absolute",
            right: "20px",
            top: "20px",
            [theme.breakpoints.up("xl")]: {
                position: "initial"
            }
        },
    }
);
