import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles(theme => ({
    root:{
        height:"100%",
        alignItems:"center",
        flexDirection:"column",
        display:"flex"
    },
    title:{
        margin: theme.spacing(1),
        textAlign:"center",
    },
    subTitle:{
        margin: theme.spacing(1),
        textAlign:"center",
    },
    subTitleNoMargin:{
        textAlign:"center",
        fontWeight:700,
        color:"#000"
    },
    subTitleLinkChangeEmail:{
        textAlign:"center",
    },
    logo: {
        marginBottom: theme.spacing(2),
    },
    changeEmail: {
        cursor: "pointer",
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(2),
    },
    buttonContainer:{
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(8),
        width:"100%"
    }
}));
export default useStyles;
