import React from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import clsx from "clsx";

import Plus from "@cvccorp-components/icons/dist/Plus";
import Minus from "@cvccorp-components/icons/dist/Minus";
import User from "@cvccorp-components/icons/dist/User";
import ArrowLeft from "@cvccorp-components/icons/dist/ArrowLeft";

//  COMPONENTS
import TextField from "../../TextField";
import Typography from "../../Typography";
import Button from "../../Button";
import Box from "../../Box";
import FilterChip from "../../FilterChip";
// import Checkbox from "../../Checkbox";
import Overlay from "../../Overlay";

//  STYLES
import { styles } from "../styles";

const Air = ({
    classes,
    error,
    errorMsg,
    open,
    fields,
    onClick,
    handleClose,
    handleApply,
    handleAdd,
    handleRemove,
    handleClasses,
    handleDirectFlight,
    handleClear,
    initialState,
    isMobile,
    height,
    ref,
}) => {
    const allPassenger = fields.adults + fields.children + fields.babies;
    const passenger =
        allPassenger <= 1
            ? `${allPassenger} Passageiro`
            : `${allPassenger} Passageiros`;
    const passengerClasses =
        fields.classes === "eco"
            ? `${passenger} - Econômica`
            : `${passenger} - Executiva`;

    const selectedPassengers = passengerClasses;

    return (
        <>
            <TextField
                label={fields.apply}
                icon={<User />}
                size="large"
                transparent
                disabled
                error={error}
                errorMsg={errorMsg}
                onClick={onClick}
                noBorder
            />
            {open && <Overlay onClick={handleClose} />}
            <Box className={`${classes.root} ${!open && classes.closed}`}>
                <div ref={ref} className={classes.container}>
                    {!isMobile ? (
                        <h1 className={classes.title}>
                            <Typography type="body1regular" className={"data-test=\"flights-search-passengers-input\""}>
                                <User size="medium" className={classes.iconUser} />
                                {passengerClasses}
                            </Typography>
                        </h1>
                    ) : (
                        <h1 className={classes.title} onClick={handleClose}>
                            <Typography type="body1regular">
                                <ArrowLeft size="medium" />
                Selecione os passageiros
                            </Typography>
                        </h1>
                    )}
                    <div className={classes.container}>
                        <div className={classes.section} style={{ maxHeight: height }}>
                            <ul className={classes.list}>
                                <h2 className={classes.paxTitle}>
                                    <Typography
                                        type="captionsemibold"
                                        className={classes.subtitlePax}
                                    >
                                        Passageiros
                                    </Typography>
                                </h2>
                                <li className={classes.listItem}>
                                    <span className={classes.content}>
                                        <Typography type="headline5">Adultos</Typography>
                                        <Typography type="caption1" className={classes.subtitle}>
                                            maior de 12 anos
                                        </Typography>
                                    </span>
                                    <div className={classes.btns}>
                                        <Button
                                            className="btn-remove-adulto"
                                            color="primary"
                                            icon={<Minus />}
                                            size="small"
                                            addRemove
                                            variant="outlined"
                                            iconPosition="center"
                                            disabled={
                                                fields.adults <= 1 || fields.babies === fields.adults
                                            }
                                            onClick={() => handleRemove("adults")}
                                        />
                                        <Typography type="subtitle" className={classes.btnLabel}>
                                            {fields.adults}
                                        </Typography>
                                        <Button
                                            className="btn-add-adulto"
                                            color="primary"
                                            icon={<Plus />}
                                            size="small"
                                            addRemove
                                            variant="outlined"
                                            iconPosition="center"
                                            disabled={allPassenger === 9}
                                            onClick={() => handleAdd("adults")}
                                        />
                                    </div>
                                </li>
                                <li className={classes.listItem}>
                                    <span className={classes.content}>
                                        <Typography type="headline5">Crianças</Typography>
                                        <Typography type="caption1" className={classes.subtitle}>
                                            de 2 até 11 anos
                                        </Typography>
                                    </span>
                                    <div className={classes.btns}>
                                        <Button
                                            className="btn-remove-crianca"
                                            color="primary"
                                            icon={<Minus />}
                                            size="small"
                                            addRemove
                                            variant="outlined"
                                            iconPosition="center"
                                            disabled={fields.children <= 0}
                                            onClick={() => handleRemove("children")}
                                        />
                                        <Typography type="subtitle" className={classes.btnLabel}>
                                            {fields.children}
                                        </Typography>
                                        <Button
                                            className="btn-add-crianca"
                                            color="primary"
                                            icon={<Plus />}
                                            size="small"
                                            addRemove
                                            variant="outlined"
                                            iconPosition="center"
                                            disabled={allPassenger === 9}
                                            onClick={() => handleAdd("children")}
                                        />
                                    </div>
                                </li>
                                <li className={classes.listItem}>
                                    <span className={classes.content}>
                                        <Typography type="headline5">Bebês</Typography>
                                        <Typography type="caption1" className={classes.subtitle}>
                                            de 0 até 23 meses
                                        </Typography>
                                    </span>
                                    <div className={classes.btns}>
                                        <Button
                                            className="btn-remove-bebes"
                                            color="primary"
                                            icon={<Minus />}
                                            size="small"
                                            addRemove
                                            variant="outlined"
                                            iconPosition="center"
                                            disabled={fields.babies <= 0}
                                            onClick={() => handleRemove("babies")}
                                        />
                                        <Typography type="subtitle" className={classes.btnLabel}>
                                            {fields.babies}
                                        </Typography>
                                        <Button
                                            className="btn-add-bebes"
                                            color="primary"
                                            icon={<Plus />}
                                            size="small"
                                            addRemove
                                            variant="outlined"
                                            iconPosition="center"
                                            disabled={
                                                fields.babies === fields.adults || allPassenger === 9
                                            }
                                            onClick={() => handleAdd("babies")}
                                        />
                                    </div>
                                </li>
                            </ul>
                        </div>
                        <h1 className={classes.title} />
                        <div>
                            <h2 className={classes.paxTitle}>
                                <Typography
                                    type="captionsemibold"
                                    className={classes.subtitlePax}
                                >
                                    Classes
                                </Typography>
                            </h2>
                            <FilterChip
                                className={clsx(classes.flightClass, "data-test=\"flights-search-passengers-class-economy\"")}
                                label="Classe econômica"
                                onClick={() => handleClasses("eco")}
                                isActive={fields.classes === "eco"}
                            />
                            <FilterChip
                                className={clsx(classes.flightClass, "data-test=\"flights-search-passengers-class-executive\"")}
                                label="Classe executiva"
                                onClick={() => handleClasses("exe")}
                                isActive={fields.classes === "exe"}
                            />
                            {/* <Checkbox
                label="Somente voo direto"
                value={fields.directFlight}
                onChange={handleDirectFlight}
                checked={fields.directFlight}
              /> */}
                        </div>
                        {!isMobile ? (
                            <div className={classes.footer}>
                                <Button
                                    className={clsx(classes.btn, "data-test=\"flights-search-passengers-clean\"")}
                                    color="primary"
                                    label="Limpar"
                                    variant="text"
                                    disabled={
                                        JSON.stringify(initialState) === JSON.stringify(fields)
                                    }
                                    onClick={handleClear}
                                />
                                <Button
                                    className={clsx(classes.btnApply, "data-test=\"flights-search-passengers-apply\"")}
                                    color="primary"
                                    label="Aplicar"
                                    variant="text"
                                    onClick={() => handleApply(selectedPassengers)}
                                />
                            </div>
                        ) : (
                            <div
                                className={
                                    open
                                        ? classes.footer
                                        : clsx(classes.footer, classes.footerClose)
                                }
                            >
                                <Button
                                    color="secondary"
                                    label={`Aplicar ${selectedPassengers}`}
                                    variant="contained"
                                    onClick={() => handleApply(selectedPassengers)}
                                    fullWidth
                                />
                            </div>
                        )}
                    </div>
                </div>
            </Box>
        </>
    );
};

Air.defaultProps = {
    height: "auto",
};

Air.propTypes = {
    classes: PropTypes.object.isRequired,
    error: PropTypes.func,
    errorMsg: PropTypes.string,
    open: PropTypes.bool,
    fields: PropTypes.object,
    onClick: PropTypes.func,
    handleClose: PropTypes.func,
    handleApply: PropTypes.func,
    handleAdd: PropTypes.func,
    handleRemove: PropTypes.func,
    handleClasses: PropTypes.func,
    handleDirectFlight: PropTypes.func,
    handleClear: PropTypes.func,
    initialState: PropTypes.object,
    isMobile: PropTypes.bool,
    height: PropTypes.string,
};

export default withStyles(styles, { withTheme: true })(Air);
