import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles(theme => ({
    root: {
        width: "100%",
        display: "flex",
        position: "relative",
        height: "100%",
        flexDirection: "column",
        [theme.breakpoints.up("md")]: {
            justifyContent: "center",
            alignItems: "center",
        },
        "& p": {
            textAlign: "center"
        },
    },
    title: {
        textAlign: "center"
    },
    subTitle: {
        marginBottom: 19,
        whiteSpace: "pre-line"
    },
    link: {
        cursor: "pointer",
        marginTop: 3,
        marginBottom: theme.spacing(3),
    },
    textCodeField: {
        margin: theme.spacing(2),
        "& fieldset": {
            borderRadius: 10,
            height: 40,
        },
        "& .MuiInputBase-input": {
            fontSize: "14px",
            height: 6,
        }
    }

}));
export default useStyles;
