/* eslint-disable @typescript-eslint/no-empty-function */
import React from "react";
import clsx from "clsx";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Grid from "@material-ui/core/Grid";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import Meal from "@cvccorp-components/icons/dist/Meal";
import Favorite from "@cvccorp-components/icons/dist/Favorite";
import Tag from "../Tag";
import LoadingContent from "../LoadingContent";
import Typography from "../Typography";
import Box from "../Box";
import Stars from "../Stars";
import Price from "../Price";
import Image from "../Image/Image";
import "../assets/css/slick-theme.css";

import { styles } from "./styles";

const fallbackImgUrl = "https://res.cloudinary.com/cvc-corp/image/upload/v1613574585/img-not-found/img-not-found-results.png";

const CardMap = ({ classes, onClick, isLoading, imgs, notAvaliable, meal, onFav, isFavorite, title, stars, product, totalPrice, oldPartialPrice, partialPrice, totalPeople, totalDays, className }) => {
    return (
        <Box className={clsx(classes.root, className)}>
            {!isLoading && (
                <div>
                    <div className={classes.tagContainer}>
                        {imgs && (
                            <div className={classes.imgContainer}>
                                <Slider infinite={false} speed={500} slidesToShow={1} touchThreshold={100} arrows dots>
                                    {imgs.map((item, i) => {
                                        const fallbackImgs = [...imgs];
                                        fallbackImgs.splice(i, 1);
                                        return (
                                            <span className={classes.imgContainer} key={i} onClick={() => onClick()}>
                                                <Image className={`${notAvaliable && classes.notAvaliableImg} ${classes.img}`} src={item} alt={item.title} width={360} height={360} fallback={[...fallbackImgs, fallbackImgUrl]} />
                                            </span>
                                        );
                                    })}
                                </Slider>
                            </div>
                        )}
                        {meal && !notAvaliable && <Tag className={imgs ? classes.topTag : ""} label={meal} icon={<Meal className={classes.mealIcon} />} color="white" />}
                        {notAvaliable && (
                            <span>
                                <Tag className={imgs ? classes.topTag : ""} label="Hotel indisponível para estas datas" color="white" />
                            </span>
                        )}
                        {onFav && <Favorite className={classes.fav} fill={isFavorite} size="medium" onClick={onFav} />}
                    </div>
                    <div className={classes.content} onClick={onClick}>
                        <div className={classes.info}>
                            <Typography className={classes.title} type="headline6">
                                {title}
                            </Typography>
                            <Typography className={classes.subtitle} type="body2regular">
                                {stars && <Stars stars={stars} size="smaller" className={classes.stars} />}
                            </Typography>
                        </div>
                        <Price className={classes.priceContainer} product={product} totalPrice={totalPrice} oldPartialPrice={oldPartialPrice} partialPrice={partialPrice} totalPeople={totalPeople} totalDays={totalDays} />
                    </div>
                </div>
            )}
            {isLoading && (
                <div>
                    <Grid container>
                        <Grid item xs={12}>
                            <div className={classes.imgContainer}>
                                <div className={classes.imgContainerPromoSmall}>
                                    <LoadingContent absolute />
                                </div>
                            </div>
                        </Grid>
                    </Grid>
                    <Grid container className={classes.content} spacing={3}>
                        <Grid item xs={6}>
                            <LoadingContent />
                        </Grid>
                        <Grid item xs={6}>
                            <LoadingContent />
                        </Grid>
                        <Grid item xs={6}>
                            <LoadingContent />
                        </Grid>
                        <Grid item xs={6}>
                            <LoadingContent />
                        </Grid>
                    </Grid>
                </div>
            )}
        </Box>
    );
};

CardMap.defaultProps = {
    imgs: [],
    isLoading: true,
    onClick: () => {},
    subtitle: "",
    meal: undefined,
    notAvaliable: false,
    isFavorite: false,
    onFav: undefined,
    stars: 0,
    className: {}
};

CardMap.propTypes = {
    classes: PropTypes.object.isRequired,
    product: PropTypes.string.isRequired,
    imgs: PropTypes.array,
    isLoading: PropTypes.bool,
    onClick: PropTypes.func,
    title: PropTypes.string.isRequired,
    meal: PropTypes.string,
    notAvaliable: PropTypes.bool,
    isFavorite: PropTypes.bool,
    onFav: PropTypes.func,
    stars: PropTypes.number,
    totalDays: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
    oldPartialPrice: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
    partialPrice: PropTypes.oneOfType([PropTypes.string, PropTypes.number]).isRequired,
    totalPrice: PropTypes.oneOfType([PropTypes.string, PropTypes.number]).isRequired,
    totalPeople: PropTypes.oneOfType([PropTypes.string, PropTypes.number]).isRequired,
    className: PropTypes.object
};

export default withStyles(styles, { withTheme: true })(CardMap);
