import { makeStyles, Theme } from "@material-ui/core/styles";

interface StyleProps {
    isMobile?: boolean;
}

const useStyles = makeStyles<Theme, StyleProps>((theme: Theme) => ({
    login: {
        height: ({ isMobile }) => isMobile ? "inherit" : "88px",
        textAlign: "right",
        // backgroundColor: "#900",
        display: "flex",
        alignItems: "center",
    },
    loginBtn: {
        textDecoration: "none",
        cursor: "pointer",
        borderColor: ({ isMobile }) => isMobile ? "#237FD0;" : "transparent",
        border: "1px solid",
        width: "120px",
        // backgroundColor: "#CCc",
        height: "32px",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        borderRadius: 8,

    },
    newLoginBtn: {
        width: "auto"
    },
    loginBtnTxt: {
        color: ({ isMobile }) => isMobile ? "#237FD0;" : "#fff",
        fontWeight: "bold",
        fontSize: "14px",
    },

    // color: #237FD0;
    // background: transparent;
    // border- color: #237FD0;
}));

export default useStyles;
