/* eslint-disable react/no-array-index-key */
/* eslint-disable no-unused-expressions */
/* eslint-disable radix */
/* eslint-disable no-nested-ternary */
import React, { useState } from "react";
import { Close } from "@cvccorp-components/icons";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Grid from "@material-ui/core/Grid";
import Luggage from "@cvccorp-components/icons/dist/Luggage";
import Help from "@cvccorp-components/icons/dist/Help";
import { FormControlLabel, Radio } from "@material-ui/core";
import Typography from "../../Typography";
import Tooltip from "../../Tooltip";
import Button from "../../Button";
import LoadingContent from "../../LoadingContent";
import AirportTag from "../../AirportTag";
import Stops from "../../Stops";
import Box from "../../Box";
import { styles } from "./styles";

const AirportTagCustom = props => {
    const { classes, iata, isHighlight, isMobile, showDiffAirport } = props;

    if (isHighlight)
        return isMobile ? (
            <span onClick={showDiffAirport}>
                <AirportTag label={iata} icon={<Help />} color="spot" />
            </span>
        ) : (
            <Tooltip className={classes.tooltip} label="Aeroporto de ida diferente do aeroporto de volta">
                <AirportTag label={iata} icon={<Help />} color="spot" />
            </Tooltip>
        );
    return <AirportTag label={iata} color="white" />;
};

const FlightItem = ({
    classes,
    id,
    onClick,
    outwardFlights,
    backFlights,
    pax,
    pricePerAdult,
    mathSignDiff,
    taxesIncluded,
    parcel,
    parcelPrice,
    peopleLabel,
    isLoading,
    isMobile,
    onSelectedSegment,
    onDetails,
    onBuy,
    selected,
    totalPrice,
    showDiffAirport
}) => {
    const [outwardSegmet, setOutwardSegmet] = useState(outwardFlights && outwardFlights[0]);
    const [backSegmet, setBackSegmet] = useState(backFlights && backFlights[0]);
    const [changeFlight, setChangeFlight] = useState(false);
    const [idx, setIdx] = useState(null);
    const [showModal, setShowModal] = useState(false);

    const renderLoadingCard = () => {
        return (
            <Grid container>
                <Grid item xs={12} lg={9}>
                    <ul className={classes.legContainer}>
                        <li className={classes.leg}>
                            <Grid container>
                                <Grid item xs={12} lg={6}>
                                    <header className={classes.header}>
                                        <Grid item xs={2}>
                                            <LoadingContent />{" "}
                                        </Grid>
                                        <Grid item xs={10}>
                                            <Grid item xs={3}>
                                                <LoadingContent />
                                            </Grid>
                                            <Grid item xs={7}>
                                                <LoadingContent />
                                            </Grid>
                                        </Grid>
                                    </header>
                                </Grid>
                                <Grid item xs={12} lg={6}>
                                    <Grid container>
                                        <Grid item xs={10}>
                                            <Grid container>
                                                <Grid item xs={2}>
                                                    <LoadingContent />
                                                    <LoadingContent />
                                                </Grid>
                                                <Grid className={classes.stops} item xs={5} align="center">
                                                    <LoadingContent />
                                                    <LoadingContent />
                                                </Grid>
                                                <Grid item xs={2}>
                                                    <LoadingContent />
                                                    <LoadingContent />
                                                </Grid>
                                            </Grid>
                                        </Grid>
                                        <Grid item xs={2}>
                                            <LoadingContent />
                                        </Grid>
                                    </Grid>
                                </Grid>
                                <Grid item xs={12} lg={10}>
                                    <div className={classes.legFooter}>
                                        <Grid item xs={3}>
                                            <LoadingContent />
                                        </Grid>
                                        <Grid item xs={2}>
                                            <LoadingContent />
                                        </Grid>
                                    </div>
                                </Grid>
                            </Grid>
                        </li>
                        <li className={classes.leg}>
                            <Grid container>
                                <Grid item xs={12} lg={6}>
                                    <header className={classes.header}>
                                        <Grid item xs={2}>
                                            <LoadingContent />{" "}
                                        </Grid>
                                        <Grid item xs={10}>
                                            <Grid item xs={3}>
                                                <LoadingContent />
                                            </Grid>
                                            <Grid item xs={7}>
                                                <LoadingContent />
                                            </Grid>
                                        </Grid>
                                    </header>
                                </Grid>
                                <Grid item xs={12} lg={6}>
                                    <Grid container>
                                        <Grid item xs={10}>
                                            <Grid container>
                                                <Grid item xs={2}>
                                                    <LoadingContent />
                                                    <LoadingContent />
                                                </Grid>
                                                <Grid className={classes.stops} item xs={5} align="center">
                                                    <LoadingContent />
                                                    <LoadingContent />
                                                </Grid>
                                                <Grid item xs={2}>
                                                    <LoadingContent />
                                                    <LoadingContent />
                                                </Grid>
                                            </Grid>
                                        </Grid>
                                        <Grid item xs={2}>
                                            <LoadingContent />
                                        </Grid>
                                    </Grid>
                                </Grid>
                                <Grid item xs={12} lg={10}>
                                    <div className={classes.legFooter}>
                                        <Grid item xs={3}>
                                            <LoadingContent />
                                        </Grid>
                                        <Grid item xs={2}>
                                            <LoadingContent />
                                        </Grid>
                                    </div>
                                </Grid>
                            </Grid>
                        </li>
                    </ul>
                </Grid>
                <Grid className={classes.price} item xs={12} lg={3}>
                    <Grid container alignItems="flex-end">
                        <Grid item xs={6} lg={12}>
                            <Grid item xs={5}>
                                <LoadingContent />
                            </Grid>

                            <Grid item xs={3}>
                                <LoadingContent />
                            </Grid>

                            <Grid item xs={4}>
                                <LoadingContent />
                            </Grid>
                            <Grid item xs={4}>
                                <LoadingContent />
                            </Grid>

                            <br />
                        </Grid>

                        <Grid item xs={6} lg={12} align="center">
                            <Grid item xs={12}>
                                <LoadingContent />
                            </Grid>
                            <Grid item xs={8}>
                                <LoadingContent />
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        );
    };

    const renderBaggageInfo = ({ luggage }) => {
        return (
            <>
                {luggage && luggage.checkedBaggageQuantity > 0 && luggage.handBaggageQuantity > 0 && (
                    <Typography type="captionregular">
                        <Luggage className={classes.luggage} size="medium" carryOn handBag checkedBag />
                        Bagagens Inclusas
                    </Typography>
                )}
                {luggage && luggage.checkedBaggageQuantity === 0 && luggage.handBaggageQuantity > 0 && (
                    <Typography type="captionregular">
                        <Luggage className={classes.luggage} size="medium" carryOn handBag />
                        Bagagem
                        <br />
                        de mão
                    </Typography>
                )}
                {luggage && luggage.checkedBaggageQuantity > 0 && luggage.handBaggageQuantity === 0 && (
                    <Typography type="captionregular">
                        <Luggage className={classes.luggage} size="medium" carryOn handBag />
                        Bagagem
                        <br />
                        de mão
                    </Typography>
                )}
                {luggage && luggage.checkedBaggageQuantity === 0 && luggage.handBaggageQuantity === 0 && (
                    <Typography type="captionregular">
                        <Luggage className={classes.luggage} size="medium" carryOn />
                        <br />
                        Sem
                        <br />
                        Bagagem
                    </Typography>
                )}
            </>
        );
    };

    const renderCardHeader = ({ cia, issuerCia, date, classFlight }, selectedIndex) => {
        const newName = selectedIndex === 0 ? "Ida" : "Volta";

        return (
            <Grid item xs={12} lg={changeFlight && idx === selectedIndex ? 4 : 5}>
                <header className={classes.header}>
                    <img className={classes.ciaLogo} src={`https://almundo-com-res.cloudinary.com/image/upload/global/logos/digital-air-companies/${issuerCia}.svg`} alt={cia} />
                    <div>
                        <Typography type="headline6">{newName}</Typography>{" "}
                        <Typography className={classes.cia} type="body2regular">
                            ({cia})
                        </Typography>
                        <Typography className={classes.date} type="body2regular">
                            {date} - {classFlight}
                        </Typography>
                    </div>
                </header>
            </Grid>
        );
    };

    const renderCardPrice = dataCallback => {
        return (
            <Grid container alignItems="flex-end">
                <Grid item xs={6} lg={12}>
                    <Typography type="body2semibold">{mathSignDiff} R$</Typography>
                    <Typography type="headline3">{pricePerAdult}</Typography>
                    <Typography type="body2"> / Adulto</Typography>
                    <span
                        style={{
                            display: "flex",
                            alignItems: "baseline"
                        }}
                    >
                        <Typography className={classes.priceLabel} type="captionregular">
                            {`${pax} ${peopleLabel}${pax > 1 ? "s" : ""}`}
                        </Typography>
                        <Typography type="captionsemibold" className={classes.priceTotalPackage}>
                            {totalPrice}
                        </Typography>
                    </span>

                    <br />
                </Grid>
                <Grid item xs={6} lg={12} align="center">
                    <Button color="primary" label={selected ? "Escolhido" : "Escolher"} variant={selected ? "outlined" : "contained"} fullWidth onClick={() => (onBuy ? onBuy(dataCallback) : null)} />
                    <Typography type="captionregular">{taxesIncluded ? "Taxas inclusas |" : "Taxas não inclusas |"}</Typography>{" "}
                    <Typography type="captionsemibold">
                        Em até {parcel}x
                        {parcelPrice && (
                            <>
                                de <span className={classes.orangeColor}>R$ {parcelPrice}</span>
                            </>
                        )}
                    </Typography>
                </Grid>
            </Grid>
        );
    };

    const renderCardTime = ({ departure, departureIata, duration, stops, arrival, arrivalIata }, highlightIata, selectedIndex) => {
        return (
            <Grid container>
                <Grid item>
                    <Typography className={classes.airport} type="headline4">
                        {departure}
                    </Typography>
                    <AirportTagCustom classes={classes} iata={departureIata} isHighlight={highlightIata && selectedIndex === 0} isMobile={isMobile} showDiffAirport={showDiffAirport} />
                </Grid>
                <Grid className={classes.stops} item xs={5} lg={8} align="center">
                    <Typography type="captionregular">
                        {duration}
                        <Stops className={classes.stopsIcon} qtd={stops} />
                        {stops > 0 ? `${stops} parada${stops > 1 ? "s" : ""}` : "direto"}
                    </Typography>
                </Grid>
                <Grid item>
                    <Typography className={classes.airport} type="headline4">
                        {arrival}
                    </Typography>
                    <AirportTagCustom classes={classes} iata={arrivalIata} isHighlight={highlightIata && selectedIndex === 1} isMobile={isMobile} showDiffAirport={showDiffAirport} />
                </Grid>
            </Grid>
        );
    };

    const renderModalMobile = item => {
        return (
            <div className={classes.modal}>
                <div className={classes.scroll}>
                    <div className={classes.close} onClick={() => setShowModal(false)}>
                        <Close />
                        <Typography type="body2semibold" style={{ paddingLeft: "10px" }}>
                            Outros horários do voo
                        </Typography>
                    </div>
                    {item}
                </div>
            </div>
        );
    };

    const moreFlights = (segment, highlightIata, checked, selectedIndex) => {
        return (
            segment &&
            segment.flights &&
            !!segment.flights.length &&
            segment.flights.slice(0, 1).map(
                (flight, index) =>
                    flight &&
                    flight.departureDateTime &&
                    flight.arrivalDateTime && (
                        <Grid container className={classes.noWrap} key={index}>
                            <>
                                <FormControlLabel
                                    style={{
                                        margin: 0
                                    }}
                                    value={flight}
                                    control={
                                        <Radio
                                            classes={{
                                                root: classes.radio,
                                                checked: classes.checked
                                            }}
                                            checked={checked}
                                            onClick={() => {
                                                if (onSelectedSegment) onSelectedSegment(segment, selectedIndex);
                                                selectedIndex === 0 ? setOutwardSegmet(segment) : setBackSegmet(segment);
                                                setIdx(selectedIndex);
                                                setTimeout(() => {
                                                    setChangeFlight(false);
                                                    setShowModal(false);
                                                }, 400);
                                            }}
                                        />
                                    }
                                />
                                <Grid item>
                                    <Typography className={classes.airport} type="headline4">
                                        {flight.departureDateTime.split("T")[1].split(":")[0]}:{flight.departureDateTime.split(":")[1]}
                                    </Typography>
                                    <AirportTagCustom classes={classes} iata={segment.departureIata} isHighlight={highlightIata && selectedIndex === 0} isMobile={isMobile} showDiffAirport={showDiffAirport} />
                                </Grid>
                                <Grid className={classes.stops} item xs={5} lg={8} align="center">
                                    <Typography type="captionregular">
                                        {segment.duration}
                                        <Stops className={classes.stopsIcon} qtd={segment.stops} />
                                        {segment.stops > 0 ? `${segment.stops} parada${segment.stops > 1 ? "s" : ""}` : "direto"}
                                    </Typography>
                                </Grid>
                                <Grid item>
                                    <Typography className={classes.airport} type="headline4">
                                        {flight.arrivalDateTime.split("T")[1].split(":")[0]}:{flight.arrivalDateTime.split(":")[1]}
                                    </Typography>
                                    <AirportTagCustom classes={classes} iata={segment.arrivalIata} isHighlight={highlightIata && selectedIndex === 1} isMobile={isMobile} showDiffAirport={showDiffAirport} />
                                </Grid>
                            </>
                        </Grid>
                    )
            )
        );
    };

    const renderSegment = (segment, segmentList, selectedIndex, highlightIata) => {
        return (
            <li className={classes.leg} key={selectedIndex}>
                <Grid container>
                    {renderCardHeader(segment, selectedIndex)}
                    <Grid item xs={12} lg={changeFlight && idx === selectedIndex ? 8 : 7}>
                        <Grid container>
                            <Grid item xs={9}>
                                {(!changeFlight && idx !== selectedIndex) || (changeFlight && idx !== selectedIndex) || (!changeFlight && idx === selectedIndex)
                                    ? renderCardTime(segment, highlightIata, selectedIndex)
                                    : changeFlight && idx === selectedIndex && segmentList && !!segmentList.length && !isMobile && segmentList.map(data => moreFlights(data, false, segment.id === data.id, selectedIndex))}
                                {idx === selectedIndex && segmentList && !!segmentList.length && isMobile && showModal && renderModalMobile(segmentList.map(data => moreFlights(data, false, segment.id === data.id, selectedIndex)))}
                            </Grid>
                            <Grid item xs={1} />
                            <Grid item xs={2} className={classes.centerContent}>
                                {renderBaggageInfo(segment)}
                            </Grid>
                        </Grid>
                    </Grid>
                    <Grid item xs={12} lg={12}>
                        <div className={classes.legFooter}>
                            <Grid item xs={0} md={0} lg={5} />
                            <Button
                                className={classes.changeTime}
                                color="primary"
                                label={changeFlight && idx === selectedIndex ? "Manter horário selecionado" : "Escolher outro horário"}
                                variant="outlined"
                                size="small"
                                fullWidth
                                // eslint-disable-next-line no-unused-expressions
                                onClick={() => {
                                    setShowModal(true);
                                    setIdx(selectedIndex);
                                    setChangeFlight(!changeFlight);
                                }}
                                disabled={false}
                            />
                            <Grid item xs={0} md={0} lg={7} />
                            <Button className={classes.detailBtn} outline label={selectedIndex === 0 ? "Detalhes da ida" : "Detalhes da volta"} variant="text" onClick={() => (onDetails ? onDetails(segment, selectedIndex) : null)} />

                            <Grid item xs={0} md={0} lg={1} />
                        </div>
                    </Grid>
                </Grid>
            </li>
        );
    };

    const highlightSelectedIata = outwardSegmet && backSegmet && outwardSegmet.departureIata !== backSegmet.arrivalIata;
    return (
        <Box className={classes.root} onClick={onClick} key={id}>
            {!isLoading && (
                <Grid container>
                    <Grid item xs={12} lg={9}>
                        <ul className={classes.legContainer}>
                            {!isLoading && renderSegment(outwardSegmet, outwardFlights, 0, highlightSelectedIata)}

                            {!isLoading && renderSegment(backSegmet, backFlights, 1, highlightSelectedIata)}
                        </ul>
                    </Grid>
                    <Grid className={classes.price} item xs={12} lg={3}>
                        {renderCardPrice({ outwardSegmet, backSegmet })}
                    </Grid>
                </Grid>
            )}
            {isLoading && renderLoadingCard()}
        </Box>
    );
};

FlightItem.propTypes = {
    classes: PropTypes.object.isRequired,
    pax: PropTypes.string,
    taxesIncluded: PropTypes.bool,
    parcel: PropTypes.string,
    onClick: PropTypes.func,
    onBuy: PropTypes.func,
    onDetails: PropTypes.func,
    isLoading: PropTypes.bool
};

export default withStyles(styles, { withTheme: true })(FlightItem);
