import { createStyles, makeStyles } from "@material-ui/styles";

export const useStyles = makeStyles(() => createStyles({
    modalDialogWrapper: {
        background: "transparent",
        boxShadow: "none",
        position: "relative",
        margin: 0,

        "& .MuiDialogContent-root": {
            width: "100%",
            height: "100%",
            padding: 0,
            position: "relative",
            overflow: "none",
            "&::-webkit-scrollbar": {
                display: "none"
            },

            display: "flex",
            flexDirection: "row-reverse",

            borderRadius: 0
        }
    },

    modalDialogButtonDesktop: {
        minWidth: "40px",
        width: "40px",
        height: "40px",
        background: "white",
        marginLeft: "12px",
        borderRadius: "16px",
        border: "none",
        cursor: "pointer",

        "& > span": {
            height: "24px",
            width: "24px"
        },

        "&:hover": {
            backgroundColor: "white"
        },

        "& svg": {
            width: "12.58px",
            height: "12.58px",
            fill: "#0007",
        },

        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 2
    },

    modalWrapper: {
        height: "100%",
        width: "100%",
        position: "relative",
        background: "white",
        display: "flex",
        borderRadius: "16px",

        "&::-webkit-scrollbar": {
            display: "none"
        }
    },

    modalContentWrapper: {
        width: "100%",
        background: "white",
        borderRadius: "16px",

        "&::-webkit-scrollbar": {
            display: "none"
        }
    }
}));
