import React, { useState } from "react";
import moment from "moment";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Calendar from "@cvccorp-components/icons/dist/Calendar";
import ArrowLeft from "@cvccorp-components/icons/dist/ArrowLeft";
import Calendar1day from "../Calendar1day";
import TextField from "../../TextField";
import Typography from "../../Typography";
import Button from "../../Button";
import Overlay from "../../Overlay";

import { styles } from "./styles";

const dateExtensiveFormat = date =>
    moment(date, "YYYY-MM-DD").format("DD [de] MMM YYYY");

const FullCalendar1day = props => {
    const { classes, date, selectedDate, isOutsideRange } = props;
    const [show, setShow] = useState(props.isOpen);

    const [startDate, setStartDate] = useState(date ? moment(date) : null);

    const setDate = async date => {
        setStartDate(date);
        !props.isMobile && date !== undefined
            ? handleClose()
            : !props.isMobile
                ? setShow(true)
                : handleOpen();

        props.onDateChange(date);
    };

    const handleOpen = e => {
        setShow(true);
        if (props.onOpen) props.onOpen(e);
        if (typeof window !== "undefined") {
            if (props.isMobile) document.body.classList.add("stop-scroll");
        }
    };

    const handleClose = e => {
        setShow(false);
        if (props.onClose) props.onClose(e);
        if (typeof window !== "undefined") {
            if (props.isMobile) document.body.classList.remove("stop-scroll");
        }
    };

    const apply = () => {
        if (startDate !== null) handleClose();
    };

    return (
        <>
            <TextField
                icon={<Calendar />}
                size="large"
                disabled
                label="Escolha a data"
                value={
                    startDate !== null
                        ? `${
                            startDate !== null
                                ? dateExtensiveFormat(startDate)
                                : props.label
                        }`
                        : ""
                }
                transparent
                onClick={() => handleOpen()}
                error={props.error}
                errorMsg={props.errorMsg}
                inputClassName={classes.input}
                labelClassName={classes.input}
                noBorder
            />{" "}
            {show && <Overlay onClick={() => handleClose()} />}{" "}
            <div className={`${classes.calendar} ${!show && classes.closed}`}>
                <div className={classes.container}>
                    <div
                        className={classes.header}
                        onClick={() => {
                            props.isMobile && handleClose();
                        }}
                    >
                        <Typography type="body1regular" className={classes.title}>
                            {" "}
                            {props.isMobile ? (
                                <ArrowLeft className={classes.icon} />
                            ) : (
                                <Calendar className={classes.icon} />
                            )}
              Escolha a data{" "}
                        </Typography>{" "}
                        <div className={classes.dates}>
                            <div className={classes.datesItem}>
                                <TextField
                                    readonly
                                    label={props.label}
                                    onFocus={() => handleOpen()}
                                    value={
                                        startDate !== null ? dateExtensiveFormat(startDate) : ""
                                    }
                                    className={classes.dateInput}
                                />{" "}
                            </div>{" "}
                        </div>{" "}
                    </div>{" "}
                    <div className={classes.content}>
                        <Calendar1day
                            date={startDate}
                            onDateChange={date => setDate(date)}
                            selectedDate={
                                selectedDate &&
                selectedDate !== undefined &&
                !!selectedDate.length &&
                selectedDate
                            }
                            isMobile={props.isMobile}
                            isOutsideRange={day =>
                                day.isBefore(moment("2020-06-01")) ||
                (isOutsideRange && isOutsideRange(day))
                            }
                        />{" "}
                    </div>{" "}
                    {props.isMobile && (
                        <div className={`${classes.footer} ${show && classes.showFooter}`}>
                            <Button
                                color="secondary"
                                variant="contained"
                                fullWidth
                                label="Aplicar"
                                onClick={() => apply()}
                            />{" "}
                        </div>
                    )}{" "}
                </div>{" "}
            </div>{" "}
        </>
    );
};

FullCalendar1day.propTypes = {
    classes: PropTypes.oneOfType([PropTypes.string, PropTypes.object]).isRequired,
    onDateChange: PropTypes.func.isRequired,
    label: PropTypes.string.isRequired,
    // eslint-disable-next-line react/require-default-props
    isOutsideRange: PropTypes.func,
};

export default withStyles(styles, {
    withTheme: true,
})(FullCalendar1day);
