import React from "react";
import { Auth } from "aws-amplify";
import { injectIntl, IntlShape } from "react-intl";
import useGa from "../../../../utils/useGa";
import AppleIcon from "../icons/AppleIcon";
import FacebookIcon from "../icons/FacebookIcon";
import GoogleIcon from "../icons/Google";
import InternalButton from "../InternalButton";
import useStyles from "./styles";
interface IBtnsSocialLogin {
    intl: IntlShape;
}

const BtnsSocialLogin: React.FC<IBtnsSocialLogin> = ({ intl }) => {
    const classes = useStyles({});
    const facebookButtonText = intl.formatMessage({ id: "MainScreen.btnFacebook" });
    const googleButtonText = intl.formatMessage({ id: "MainScreen.btnGoogle" });
    const appleButtonText = intl.formatMessage({ id: "MainScreen.btnApple" });
    const { saveOnGa } = useGa();
    const FacebookSocialLogin = () => new Promise(async (resolve) => {
        saveOnGa({
            event: "login_event",
            category: "Account",
            action: "Form Login",
            label: "login :: facebook"
        })
        Auth.federatedSignIn({ customProvider: "Facebook" });
        return resolve;
    });
    const GoogleSocialLogin = () => new Promise(async (resolve) => {
        saveOnGa({
            event: "login_event",
            category: "Account",
            action: "Form Login",
            label: "login :: google"
        });
        Auth.federatedSignIn({ customProvider: "Google" });
        return resolve;
    });
    const AppleSocialLogin = () => new Promise(async (resolve) => {
        saveOnGa({
            event: "login_event",
            category: "Account",
            action: "Form Login",
            label: "login :: apple"
        });
        Auth.federatedSignIn({ customProvider: "SignInWithApple" });
        return resolve;
    });
    return (
        <div className={`${classes.socialButtonsWrapper}`}>
            <InternalButton handlerClick={FacebookSocialLogin} isSocialNetwork icon={FacebookIcon} type="button" text={facebookButtonText} />
            <InternalButton handlerClick={GoogleSocialLogin} isSocialNetwork icon={GoogleIcon} type="button" text={googleButtonText} />
            <InternalButton handlerClick={AppleSocialLogin} isSocialNetwork icon={AppleIcon} type="button" text={appleButtonText} />
        </div>
    );
};

export default injectIntl(BtnsSocialLogin);
