import { createMuiTheme } from "@material-ui/core/styles";
import "../../assets/css/font.css";

const backdrop = "rgba(0,0,0, .3)";
const backdrop70 = "rgba(0,0,0, .7)";
const backdrop100 = "#000";

const bold = "HindMBold";
const regular = "HindMRegular";
const semibold = "HindMSemiBold";

const ThemeCorp = createMuiTheme({
    themeName: "blackandwhite",
    breakpoints: {
        values: {
            xs: 0,
            sm: 600,
            md: 960,
            lg: 1024,
            xl: 1280
        }
    },
    palette: {
        primary: {
            lighter: "#000",
            light: "#000",
            main: "#000",
            dark: "#000",
            darkHover: "#000",
            alpha: "#000"
        },
        secondary: {
            lighter: "#000",
            light: "#000",
            main: "#000",
            dark: "#000",
            contrastText: "#fff"
        },
        error: { main: "#000" },
        alert: { main: "#000" },
        success: {
            main: "#000",
            hover: "#000"
        },
        text: {
            primary: "#000",
            secondary: "#000",
            disabled: "#000"
        },
        grey: {
            50: "#000",
            1000: "#000"
        },
        tabs: {
            main: "#000",
            hover: "#000"
        },
        spot: { main: "#000" },
        hotel: { main: "#000" },
        air: { main: "#000" },
        package: { main: "#000" },
        car: { main: "#000" },
        white: {
            main: "#fff",
            alpha: "#fff"
        },
        backdrop: {
            main: backdrop,
            dark: backdrop70,
            darker: backdrop100
        },
        canceled: { main: "#000" },
        approved: { main: "#000" },
        processed: { main: "#000" },
        greyscale: { 3: "#CACACA" }
    },
    typography: {
        useNextVariants: true,
        color: "#000",
        fontFamily: regular,
        fontSize: 16,
        h1: {
            fontFamily: bold,
            fontSize: "48px",
            lineHeight: "60px"
        },
        h2: {
            fontFamily: bold,
            fontSize: "36px",
            lineHeight: "40px"
        },
        h3: {
            fontFamily: bold,
            fontSize: "26px",
            lineHeight: "30px"
        },
        h4: {
            fontFamily: bold,
            fontSize: "22px",
            lineHeight: "28px"
        },
        h5: {
            fontFamily: bold,
            fontSize: "20px",
            lineHeight: "26px"
        },
        h6: {
            fontFamily: bold,
            fontSize: "18px",
            lineHeight: "24px"
        },
        subtitle1: { fontSize: "18px", lineHeight: "22px" },
        subtitle2: { fontSize: "16px", lineHeight: "20px" },
        subtitle3: { fontSize: "14px", lineHeight: "18px" },
        subtitle4: { fontSize: "12px", lineHeight: "16px" },
        caption: {
            color: "#000",
            fontFamily: bold,
            fontSize: "14px",
            lineHeight: "18px"
        },
        body1: { fontSize: "16px" },
        body2: { fontSize: "14px" },
        body3: { fontSize: "12px" }
    },
    font: {
        regular,
        semibold,
        bold
    },
    shadows: [
        "0px 0px 0px 0px rgba(0,0,0,0.1)",
        "0px 0px 1px 0px rgba(0,0,0,0.1)",
        "0px 0px 2px 0px rgba(0,0,0,0.1)",
        "0px 0px 3px 0px rgba(0,0,0,0.1)",
        "0px 0px 4px 0px rgba(0,0,0,0.1)",
        "0px 0px 5px 0px rgba(0,0,0,0.1)",
        "0px 0px 6px 0px rgba(0,0,0,0.1)",
        "0px 0px 7px 0px rgba(0,0,0,0.1)",
        "0px 0px 8px 0px rgba(0,0,0,0.1)",
        "0px 0px 9px 0px rgba(0,0,0,0.1)",
        "0px 0px 10px 0px rgba(0,0,0,0.1)",
        "0px 0px 11px 0px rgba(0,0,0,0.1)",
        "0px 0px 12px 0px rgba(0,0,0,0.1)",
        "0px 0px 13px 0px rgba(0,0,0,0.1)",
        "0px 0px 14px 0px rgba(0,0,0,0.1)",
        "0px 0px 15px 0px rgba(0,0,0,0.1)",
        "0px 0px 16px 0px rgba(0,0,0,0.1)",
        "0px 0px 17px 0px rgba(0,0,0,0.1)",
        "0px 0px 18px 0px rgba(0,0,0,0.1)",
        "0px 0px 19px 0px rgba(0,0,0,0.1)",
        "0px 0px 20px 0px rgba(0,0,0,0.1)",
        "0px 0px 21px 0px rgba(0,0,0,0.1)",
        "0px 0px 22px 0px rgba(0,0,0,0.1)",
        "0px 0px 23px 0px rgba(0,0,0,0.1)",
        "0px 0px 24px 0px rgba(0,0,0,0.1)"
