import React, { useContext, useEffect } from "react";
import { Typography, withWidth, Link } from "@material-ui/core";
import { WithWidth, isWidthDown } from "@material-ui/core/withWidth";
import Cookies from "universal-cookie";
import { BrandContext } from "../../../BrandContext";
import { useAuthAmplifyContext } from "../../contexts/AmplifyContext";
import { useLoadingContext } from "../../contexts/LoadingContext";
import { useRenderSteps } from "../../contexts/renderSteps";
import HeaderLogin from "../../shared/components/HeaderLogin";
import InternalButton from "../../shared/components/InternalButton";
import Loading from "../../shared/components/Loading";
import Terms from "../../shared/components/Terms";
import { useUserData } from "../../shared/hooks/UserContext";
import api from "../../shared/services/api";
import { usersInProvider } from "../MainScreen/utils";
import { useProfileStyles } from "./styles";

interface Props extends WithWidth {
    onCloseClick?(): void;
    isModal?: boolean;
}

const ProfileCorp: React.FC<Props> = ({ width, onCloseClick, isModal }) => {
    const { user } = useUserData();
    const ctx = useContext(BrandContext);
    const { renderNewStep, setPreviousStep, previousStep } = useRenderSteps();
    const isMobile = isWidthDown("sm", width);
    const { setAuthAmplify } = useAuthAmplifyContext();
    const { setLoading, loading } = useLoadingContext();
    const styles = useProfileStyles({
        isMobile
    });

    const handleNavigateToCorp = () => {
        let environment = "";
        if (window.location.href.includes("qa.")) {
            environment = "qa.";
        } else if (window.location.href.includes("ti.")) {
            environment = "ti.";
        } else if (window.location.href.includes("localhost")) {
            environment = "ti.";
        }

        const cookies = new Cookies();
        cookies.set("corpAgentId", btoa(user.email), {
            path: "/"
        });

        window.open(`https://${environment}cvc.com.br/venda-assistida/login`, "_blank");
    };
    useEffect(() => {
        setPreviousStep("MainScreen");
    }, []);
    const handleNavigateAsUser = async () => {
        setLoading(true);
        const { email } = user;

        const response = await api.get(`/apigw/authentication/${ctx.brand}/user-exists?email=${email}`);

        const userExists = usersInProvider(response.data.found, true);
        setLoading(false);
        setPreviousStep("ProfileCorp");
        if (userExists?.nextStep) {
            if (!userExists.emailConfirmed) {
                setAuthAmplify("confirmSignUp");
            }

            renderNewStep(userExists.nextStep);
        } else {
            renderNewStep("ConfirmMainScreen");
        }
    };
    return loading ? (
        <Loading />
    ) : (
        <>
            <div className={styles.root}>
                <HeaderLogin isModal={isModal} previousStep={previousStep} onCloseClick={onCloseClick} />
                <div className={styles.centeredContent}>
                    <Typography variant="subtitle1">Perfil Corporativo</Typography>
                    <Typography variant="body2">
                        Identificamos uma conta corporativa usando o e-mail <strong>{user.email || "email@cvc.com.br"}</strong>, sinalize abaixo com qual perfil vocÃª quer utilizar.
                    </Typography>
                </div>
                <InternalButton type="button" text="Entrar com a conta corporativa" handlerClick={handleNavigateToCorp} />
                <Link target="_blank" onClick={handleNavigateAsUser}>
                    <Typography variant="caption" style={{ fontWeight: 700, fontSize: "12px" }}>
                        Continuar como cliente CVC
                    </Typography>
                </Link>
            </div>
            <Terms />
        </>
    );
};

export default withWidth()(ProfileCorp);
