import { makeStyles } from "@material-ui/core/styles";


interface ContainerProps {
    type?: "success" | "error";
    isMobile: boolean
}

const useStyles = makeStyles(() => ({
    root: {},
    container: (props: ContainerProps) => ({
        fontFamily: "HindMRegular",
        width: "634px",
        height: "auto",
        padding: "16px 16px 16px 20px",
        borderRadius: "12px",
        boxShadow: "2px 2px 8px rgba(0, 0, 0, 0.2)",
        display: "flex",
        alignItems: "center",
        background: `${props.type === "error" ? "#FF8B00" : "#67C453"}`,
        color: "#fff",
    }),
    toastWrapper: (props: ContainerProps) => ({
        flex: 1,
        display: "flex",
        alignItems: "center",
        fontSize: "14px",
        flexDirection: props.isMobile ? "column" : "row",
        opacity: "0.8",
        lineHeight: "20px",
    }),
    description: {

        margin: 0,
        paddingLeft: "5px",
    },
    closeButton: {
        height: "20px",
        width: "20px",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "transparent",
        border: "none",
        fontWeight: "bold",
        color: "#fff",
        cursor: "pointer",
        fontSize: "16px",
        "&:focus": {
            outline: "none"
        }
    },

}));

export default useStyles;
