import React, { useState } from "react";
import { Paper, Divider, Typography, Grid } from "@material-ui/core";
import { makeStyles , MuiThemeProvider } from "@material-ui/core/styles";
import { storiesOf } from "@storybook/react";
import themes from "../../Theme/themes";
import PaxAir from "../Air";
import { styles } from "../styles";

const useStyles = makeStyles((theme) => ({
    ...styles,
    rootBg: {
        background: theme.palette.primary.main,
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
}));

const PaxAirStories = ({
    mode, brand, isMobile, error, errorMsg
}) => {
    const classes = useStyles(themes[brand]);
    const initialState = {
        apply: "Selecione os passageiros",
        adults: 1,
        children: 0,
        babies: 0,
        classes: "eco",
        directFlight: false,
    };

    const [open, setOpen] = useState(false);
    const [fields, setFields] = useState(initialState);

    const handleOpen = () => {
        setOpen(!open);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleAdd = (field) => {
        const values = { ...fields };
        values[field] = values[field] + 1;
        setFields(values);
    };

    const handleRemove = (field) => {
        const values = { ...fields };
        values[field] = values[field] - 1;
        setFields(values);
    };

    const handleClasses = (field) => {
        const values = { ...fields };
        values.classes = field;
        setFields(values);
    };
    const handleApply = (field) => {
        const values = { ...fields };
        values.apply = field;
        setFields(values);
    };
    const handleDirectFlight = () => {
        const values = { ...fields };
        values.directFlight = !values.directFlight;
        setFields(values);
    };

    const handleClear = () => {
        setFields(initialState);
    };

    return (
        <MuiThemeProvider theme={themes[brand]}>
            <Paper className={classes[mode]} style={{ background: "#237FD0" }}>
                <Typography variant="h3">Pax Air</Typography>
                <br />
                <Divider />
                <br />
                <Grid container direction="row" justify="flex-start" spacing={2}>
                    <Grid item xs={12}>
                        <PaxAir
                            isMobile={isMobile}
                            initialState={initialState}
                            open={open}
                            fields={fields}
                            onClick={handleOpen}
                            handleClose={handleClose}
                            handleAdd={handleAdd}
                            handleRemove={handleRemove}
                            handleClear={handleClear}
                            handleClasses={handleClasses}
                            handleDirectFlight={handleDirectFlight}
                            handleApply={handleApply}
                            error={error}
                            errorMsg={errorMsg}
                        />
                    </Grid>
                </Grid>
            </Paper>
        </MuiThemeProvider>
    );
};

const argsControls = {
    args: {
        brand: "Sub",
        mode: "rootBg",
        isMobile: false,
        error: false,
        errorMsg: "Error message"
    },
    argTypes: {
        brand: {
            type: "select",
            options: ["BlackAndWhite", "BlackFriday", "CVC", "CVCCorp", "CVCYellow", "Sub"]
        },
        mode: {
            type: "select",
            options: ["rootBg", "darkBg"]
        },
        isMobile: { type: "boolean" },
        error: { type: "boolean" },
        errorMsg: { type: "string" },
    }
}

storiesOf("Components/Pax/PaxAir", module).addParameters({ storySource: { source: __STORY__, locationsMap: __LOCATIONS_MAP__ } })
    .add("Default", (args) => <PaxAirStories {...args}/>, argsControls);
