export const styles = (theme) => ({
    root: {},
    message: { width: "100%" },
    error: { background: `${theme.palette.error.main}` },
    success: { background: `${theme.palette.success.main}` },
    content: {
        alignItems: "center",
        display: "flex",
        justifyContent: "space-between",
    },
    text: {
        alignItems: "center",
        display: "flex",
    },
    closeButton: {
        cursor: "pointer",
        color: "#fff",
        background: "transparent",
        border: "0",
    },
    spacing: { marginRight: "10px" },
});
