import { makeStyles, Theme } from "@material-ui/core/styles";

const useStyles = makeStyles((theme: Theme) => ({
    root: {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: `calc(100% - ${theme.spacing(5)}px)`,
        width: "100%",
        position: "absolute",
    },

}));
export default useStyles;
