/* eslint-disable no-self-compare */

import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import createNumberMask from "text-mask-addons/dist/createNumberMask";
import { withStyles } from "@material-ui/core/styles";
import Slider from "../Slider";
import Filter from "../Filter";
import Typography from "../Typography";
import TextField from "../TextField";
import { styles } from "./styles";

const FilterRange = props => {
    const {
        rangeValue,
        onApply,
        label,
        onReset,
        noModal,
        className,
        maskOptions,
        isMobile,
        open,
        onOpen,
        onClose,
        values,
        height,
        filterTitle,
    } = props;

    const disabled = JSON.stringify(rangeValue) === JSON.stringify(values);

    const [isApply, setIsApply] = useState(!disabled);
    const [isReset, setIsReset] = useState(false);

    const isActive = () => {
        if (JSON.stringify(rangeValue) === JSON.stringify(values) && isApply) {
            setIsApply(false);
        }
        return !noModal && JSON.stringify(rangeValue) !== JSON.stringify(values);
    };

    const value = filterTitle || "";

    const _onApply = () => {
        setIsApply(true);
        onApply(
            `${maskOptions ? maskOptions.prefix : ""}${rangeValue.min} - ${maskOptions ? maskOptions.prefix : ""
            } ${rangeValue.max}`
        );
    };

    const _onClose = () => {
        setIsApply(true);
        onClose(
            `${maskOptions ? maskOptions.prefix : ""}${rangeValue.min} - ${maskOptions ? maskOptions.prefix : ""
            } ${rangeValue.max}`
        );
    };

    const Child = () => (
        <ContentRange {...props} />
    );

    useEffect(() => {
        if (isReset) {
            setIsApply(false);
            setIsReset(false);
            onReset();
        }
    }, [isReset]);

    return (
        <Filter
            className={className}
            isList
            isMobile={isMobile}
            noModal={noModal}
            label={label}
            height={height}
            items={[!isReset ? <ContentRange {...props} /> : <Child />]}
            onOpen={() => onOpen()}
            isOpen={open}
            onApply={_onApply}
            onReset={() => {
                setIsReset(true);
            }}
            onClose={_onClose}
            isActive={isActive() && isApply}
            value={[value]}
            disabled={disabled}
            isReset={isReset}
        />
    );
};

FilterRange.defaultProps = {
    classes: undefined,
    onApply: undefined,
    onReset: undefined,
    noModal: false,
    onChange: undefined,
    className: undefined,
    isMobile: false,
    step: 50,
    open: false,
    onOpen: undefined,
    onClose: undefined,
    handleFocus: undefined,
    handleBlur: undefined,
    handleChange: undefined,
};

FilterRange.propTypes = {
    classes: PropTypes.oneOfType([PropTypes.object, PropTypes.string]),
    onApply: PropTypes.func,
    onReset: PropTypes.func,
    noModal: PropTypes.bool,
    onChange: PropTypes.func,
    className: PropTypes.oneOfType([PropTypes.object, PropTypes.string]),
    isMobile: PropTypes.bool,
    step: PropTypes.number,
    open: PropTypes.bool,
    onOpen: PropTypes.func,
    onClose: PropTypes.func,
    handleFocus: PropTypes.func,
    handleBlur: PropTypes.func,
    handleChange: PropTypes.func,
};

const ContentRange = ({
    classes,
    rangeValue,
    onChangeCommitted,
    maskOptions,
    step,
    rangeTitleValues,
    handleFocus,
    handleBlur,
    handleChange,
    values,
}) => {
    const currencyMask = createNumberMask({
        ...maskOptions,
    });
    const [sliderValues, setSliderValues] = useState({ ...rangeValue });
    return (
        <>
            <Typography type="captionregular" className={classes.text}>
                de <span className={classes.highlight}>R$ {rangeTitleValues.min}</span>{" "}
        at√© <span className={classes.highlight}>R$ {rangeTitleValues.max}</span>
            </Typography>
            <Slider
                handleChange={v => {
                    setSliderValues({ min: v[0], max: v[1] });
                }}
                onChangeCommitted={(event, v) => {
                    onChangeCommitted({ min: v[0], max: v[1] });
                }}
                values={[sliderValues.min, sliderValues.max]}
                min={values.min}
                max={values.max}
                step={step}
                prefix={maskOptions.prefix}
            />
            <div className={classes.inputContainer}>
                <TextField
                    placeholder={maskOptions.prefix}
                    currencyMask={currencyMask}
                    label="Inicial"
                    rootClassName={classes.input}
                    value={`${sliderValues.min}`}
                    onFocus={() => handleFocus("min")}
                    onBlur={() => handleBlur("min")}
                    onChange={e => {
                        const replaceValue = e.target.value.replace(maskOptions.prefix, "").replace(".", "");
                        if (Number(replaceValue) || replaceValue === 0 || replaceValue === "") {
                            const newRange = {
                                min: Number(replaceValue),
                                max: sliderValues.max
                            };
                            setSliderValues({ min: newRange.min, max: newRange.max });
                            handleChange(e.target.value, "min");
                        }
                    }}
                    error={false}
                    type="text"
                    mask
                />
                <TextField
                    placeholder={maskOptions.prefix}
                    currencyMask={currencyMask}
                    label="Final"
                    rootClassName={classes.input}
                    value={`${sliderValues.max}`}
                    onFocus={() => handleFocus("max")}
                    onBlur={() => handleBlur("max")}
                    onChange={e => {
                        const replaceValue =  e.target.value.replace(maskOptions.prefix, "").replace(".", "");
                        if (Number(replaceValue) || replaceValue === 0 || replaceValue === "") {
                            const newRange = {
                                min: sliderValues.min,
                                max: Number(replaceValue),
                            };
                            setSliderValues({ min: newRange.min, max: newRange.max });
                            handleChange(e.target.value, "max");
                        }
                    }}
                    error={false}
                    type="text"
                    mask
                />
            </div>
        </>
    );
};

export default withStyles(styles, { withTheme: true })(FilterRange);
