import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles(theme => ({
    root: {
        width: "100%",
        position: "relative",
        height: "100%",
        display: "flex",
        flexDirection: "column",
        [theme.breakpoints.up("md")]: {
            justifyContent: "center",
            alignItems: "center",
        },
        "& p": {
            textAlign: "center"
        },
    },
    title: {
        textAlign: "center",
    },
    subTitle: {
        fontSize: 16
    },
    buttonContainer: {
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(4),
        width: "100%"
    },
    logo: {
        marginBottom: theme.spacing(2),
    },
    verticalSpace: {
        marginBottom: theme.spacing(2),
    },
    cautionText: {
        fontSize: 14,
        marginTop: 5
    },
    changeEmail: {
        cursor: "pointer",
    },
    subTitleLinkChangeEmail: {
        textAlign: "center",
        fontWeight: "bold",
    },
}));
export default useStyles;
