import React, { useState } from "react";
import PropTypes from "prop-types";
import Grid from "@material-ui/core/Grid";
import withStyles from "@material-ui/core/styles/withStyles";
import WithTax from "@cvccorp-components/icons/dist/WithTax";
import Typography from "../Typography";
import TextField from "../TextField";
import Button from "../Button";
import Box from "../Box";
import { styles } from "./styles";

const Newsletter = ({ classes, onClick, isMobile, title, description, message }) => {
    const [email, setEmail] = useState({
        error: false,
        errorMsg: "",
        value: ""
    });
    const [name, setName] = useState({
        error: false,
        errorMsg: "",
        value: ""
    });
    const [success, setSuccess] = useState(false);

    const handleChangeEmail = e => {
        setEmail({
            value: e.target.value,
            errorMsg: "",
            error: false
        });
    };

    const handleChangeName = e => {
        setName({
            value: e.target.value,
            errorMsg: "",
            error: false
        });
    };

    const isValid = () => {
        let valid = true;
        if (!/^[a-zA-Z0-9._+%-]+@[a-zA-Z0-9]+\.[A-Za-z.]+$/.test(email.value)) {
            setEmail({
                ...email,
                errorMsg: "E-mail inv치lido",
                error: true
            });
            valid = false;
        }
        if (name.value === "") {
            setName({
                ...name,
                errorMsg: "Nome inv치lido",
                error: true
            });
            valid = false;
        }
        return valid;
    };

    const handleOnClick = () => {
        if (isValid()) {
            if (onClick) {
                setSuccess(true);
                onClick({
                    name,
                    email
                });
            }
        }
    };

    return (
        <Box className={`${classes.root} ${success && classes.successBox}`}>
            {success ? (
                <>
                    <WithTax className={classes.icon} size="large" />
                    <Typography type={isMobile ? "headline6" : "headline2"}>
                        Obrigado pela sua <span className={classes.title}> assinatura! </span>{" "}
                    </Typography>
                    <Typography type={isMobile ? "body2regular" : "body1regular"}>{message}</Typography>
                </>
            ) : (
                <>
                    <Grid container spacing={isMobile ? 1 : 2} className={classes.grid}>
                        <Grid item xs={12} className={classes.gridTitle}>
                            <Typography type={isMobile ? "headline6" : "headline2"}>{title}</Typography>
                        </Grid>
                        <Grid container spacing={1} alignItems="center" className={classes.gridData}>
                            <Grid item xs={12} lg={3}>
                                <TextField onChange={handleChangeName} label="Digite seu nome" size="large" error={name.error} errorMsg={name.errorMsg} />
                            </Grid>
                            <Grid item xs={12} lg={6}>
                                <TextField onChange={handleChangeEmail} label="Digite seu email" size="large" error={email.error} errorMsg={email.errorMsg} />
                            </Grid>
                            <Grid item xs={12} lg={3}>
                                <Button color="primary" label="Assinar" size="large" variant="contained" onClick={handleOnClick} fullWidth />
                            </Grid>
                        </Grid>
                    </Grid>
                    <Grid container spacing={1} alignItems="center" className={classes.gridInfo}>
                        <Typography type={isMobile ? "captionregular" : "body2regular"} className={classes.text}>
                            {description}
                        </Typography>
                    </Grid>
                </>
            )}{" "}
        </Box>
    );
};

Newsletter.defaultProps = {
    classes: undefined,
    onClick: undefined,
    isMobile: false
};

Newsletter.propTypes = {
    onClick: PropTypes.func,
    isMobile: PropTypes.bool,
    classes: PropTypes.oneOfType([PropTypes.object, PropTypes.string]),
    title: PropTypes.oneOfType([PropTypes.object.isRequired, PropTypes.string.isRequired]),
    description: PropTypes.oneOfType([PropTypes.object.isRequired, PropTypes.string.isRequired]),
    message: PropTypes.oneOfType([PropTypes.object.isRequired, PropTypes.string.isRequired])
};

export default withStyles(styles, {
    withTheme: true
})(Newsletter);
