import React from "react";
import Typography from "@material-ui/core/Typography";
import { jsxDecorator } from "storybook-addon-jsx";
import { Paper, Divider } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import { makeStyles, MuiThemeProvider } from "@material-ui/core/styles";
import { storiesOf } from "@storybook/react";
import { muiTheme } from "storybook-addon-material-ui";
import { Filter } from "@cvccorp-components/icons";
import Theme from "../../Theme";
import themes from "../../Theme/themes";
import CustomChip from "../../CustomChip";
import { BrandType } from "../../types/brand";

const useStyles = makeStyles((theme) => ({
    root: {
        boxShadow: theme.shadows[24],
        padding: "30px",
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        padding: "20px",
    },
}));

type ChipsProps = {
    label: string,
    mode: string,
    brand: BrandType,
    color: string,
    variant: string,
    disabled: boolean
}

const ChipsButtons = ({brand, mode, label, color, variant, disabled}: ChipsProps): JSX.Element => {
    const classes = useStyles(themes[brand]);

    return (
        <MuiThemeProvider theme={themes[brand]}>
            <Paper className={classes[mode]} id="botoes">
                <Typography variant="h3">Custom Chips</Typography>
                <br />
                <Divider />
                <Grid container direction="column" justify="center" spacing={2}>
                    <Grid item xs={12}>
                        <br />
                        <CustomChip label={label} variant={variant} disabled={disabled} color={color}/>
                    </Grid>
                    <Grid item xs={12}>
                        <Typography variant="subtitle1">Chips com Ícone</Typography>
                        <br />
                        <CustomChip label={label} disabled={disabled} icon={<Filter />} />
                    </Grid>
                </Grid>
            </Paper>
        </MuiThemeProvider>
    );
};

const argsControls = {
    args: {
        label: 'Button Text',
        brand: "CVC",
        mode: "root",
        color: 'default',
        variant: 'contained',
        disabled: false
    },
    argTypes: {
        label: { type: 'string' },
        brand: {
            type: "select",
            options: ["BlackAndWhite", "BlackFriday", "CVC", "CVCCorp", "CVCYellow", "Sub"]
        },
        mode: {
            type: "select",
            options: ["root", "darkBg"]
        },
        color: {
            options: ['default', 'primary', 'secondary'],
            control: { type: 'select' }
        },
        variant: {
            options: ['outlined', 'contained'],
            control: { type: 'radio' }
        },
        disabled: { type: 'boolean' }
    }
}

storiesOf("Components/Buttons/CustomChip", module).addParameters({ storySource: { source: __STORY__, locationsMap: __LOCATIONS_MAP__ } })
    .addDecorator(muiTheme([Theme]))
    .addDecorator(jsxDecorator)
    .add("Default", (args) => <ChipsButtons {...args} />, argsControls);
