import React from "react";
import { User, ArrowLeft } from "@cvccorp-components/icons";
import { Paper, Divider, } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import { storiesOf } from "@storybook/react";
import { BrandContext } from "../../BrandContext/BrandContext";
import ContentCard from "../../ContentCard";
import themes from "../../Theme/themes";
import ThemeProviderCustom from "../../ThemeProviderCustom";
import { BrandType } from "../../types/brand";
import mockBrands from "../../utils/brandMock";
import SeoText from "../SeoText";

const useStyles = makeStyles((theme) => ({
    root: {
        background: theme.palette.background.default,
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
}));

type SeoTextStoryProps = {
    mode: string,
    brand: BrandType,
    title: string,
    subTitle: string
}

const SeoTextStory = ({
    mode, brand, title, subTitle
}: SeoTextStoryProps) => {
    const classes = useStyles(themes[brand]);

    return (
        <BrandContext.Provider value={mockBrands[brand]}>
            <ThemeProviderCustom>
                <Paper className={classes[mode]} id="SeoText">
                    <Typography variant="h3">SeoText</Typography>
                    <br />
                    <Divider />
                    <br />
                    <Grid>
                        <SeoText
                            title={title}
                            subTitle={subTitle}
                            content={[
                                <ContentCard
                                    icon={<User size="large" />}
                                    key="1"
                                    title="As melhores passagens aéreas para a sua trip"
                                    text="Aqui você encontra as melhores opções de passagens para a sua trip ser inesquecível."
                                    href="https://www.submarinoviagens.com.br/promocoes"
                                    link="Confira as promoções"
                                    titleHeight="5"                               
                                />,
                                <ContentCard
                                    key="2"
                                    icon={<ArrowLeft size="large" />}
                                    title="Você tem as melhores condições de pagamento"
                                    text="Queremos que você tenha a melhor experiência nas suas viagens, as reservas estão disponíveis por preços diferenciados e em até 12x sem juros, com cartão de crédito ou à vista."
                                    link="Formas de pagamento"
                                    href="https://www.submarinoviagens.com.br/institucional/formas-de-pagamento" 
                                    titleHeight="5"                               
                                />
                            ]}
                            isMobile={false}
                        />
                    </Grid>
                </Paper>
            </ThemeProviderCustom>
        </BrandContext.Provider>

    );
};

const argsControls = {
    args: {
        brand: "cvc",
        mode: "root",
        title: "Bora Viajar",
        subTitle: "Serviços que cabem no seu bolso"
    },
    argTypes: {
        brand: {
            type: "select",
            options: ["cvc", "cvccorp", "sub"]
        },
        mode: {
            type: "select",
            options: ["root", "darkBg"]
        },
        title: { type: "string" },
        subTitle: { type: "string" }
    }
}

storiesOf("Components/SeoText", module).addParameters({ storySource: { source: __STORY__, locationsMap: __LOCATIONS_MAP__ } })
    .add("Default", (args) => <SeoTextStory {...args}/>, argsControls);
