export const styles = theme => ({
    root: {
        border: "none",
        borderRadius: "16px",
        cursor: "pointer",
        height: "90px",
        overflow: "hidden",
        padding: 0,
        width: "305px",
    },
    img: {
        display: "block",
        left: "50%",
        position: "absolute",
        transform: "translate(-50%, -50%)",
        top: "50%",
        width: "100%",
    },
    container: {
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        padding: `0 ${theme.spacing(2)}px`,
    },
    icon: { marginRight: theme.spacing(1) },
    title: {
        display: "block",
        marginTop: theme.spacing(3),
    },
    subtitle: {
        display: "block",
        margin: `${theme.spacing(1) / 2}px 0`,
    },
    subtitleHighlight: {
        color: theme.palette.text.secondary,
    },
    imgContainer: {
        display: "block",
        position: "relative",
        marginLeft: theme.spacing(2) * -1,
        marginTop: theme.spacing(2) * -1,
        width: `calc(100% + ${theme.spacing(4)}px)`,
    },
    imgContainerHighlight: {
        display: "block",
        height: "90px",
        overflow: "hidden",
        position: "relative",
    },
});
