import React from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Tooltip from "../Tooltip";
import { styles } from "./styles";

const uuidv4 = require("uuid/v4");

const Amenities = ({ classes, amenitiesToShow, amenities, className, showTooltip }) => {
    const totalAmenitiesToShow = amenitiesToShow || amenities.length;

    return (
        <ul className={`${classes.root} ${className}`}>
            {amenities.map((item, i) => {
                if (i < totalAmenitiesToShow) {
                    return (
                        <li className={classes.item} key={uuidv4()}>
                            {showTooltip && item.props !== undefined && item.props.title !== undefined ? <Tooltip label={item.props.title}>{item}</Tooltip> : item}
                        </li>
                    );
                }
                return null;
            })}
        </ul>
    );
};

Amenities.propTypes = {
    classes: PropTypes.object.isRequired,
    amenities: PropTypes.array.isRequired,
    amenitiesToShow: PropTypes.number,
    className: PropTypes.object,
    showTooltip: PropTypes.bool
};

export default withStyles(styles, { withTheme: true })(Amenities);
