import React, { useContext } from "react";
import Link from "@material-ui/core/Link";
import { injectIntl, IntlShape } from "react-intl";
import { BrandContext } from "../../../BrandContext";
import { Brand } from "../../../BrandContext/BrandContext";
import useGa from "../../../utils/useGa";
import { useRenderSteps } from "../../contexts/renderSteps";
import HeaderLogin from "../../shared/components/HeaderLogin";
import InternalButton from "../../shared/components/InternalButton";
import SubTitle from "../../shared/components/SubTitle";
import Terms from "../../shared/components/Terms";
import Title from "../../shared/components/Title";
import { useUserData } from "../../shared/hooks/UserContext";
import translate from "../../shared/i18n/translate";
import useStyles from "./styles";

type TConfirmMainScreen = {
    intl: IntlShape;
};

interface RenderStepsSwitchProps extends TConfirmMainScreen {
    onCloseClick?(): void;
    isModal?: boolean;
}

const ConfirmMainScreen: React.FC<RenderStepsSwitchProps> = ({ intl, onCloseClick, isModal }) => {
    const classes = useStyles({});
    const { saveOnGa } = useGa();
    const { renderNewStep, setPreviousStep, previousStep } = useRenderSteps();
    const { user, setUser } = useUserData();

    const ctx = useContext(BrandContext);

    const handleCreateAccount = (): Promise<string> =>
        new Promise(resolve => {
            saveOnGa({
                event: "login_event",
                category: "Account",
                action: "Registration",
                label: "step 1 | confirm account creation"
            });
            setPreviousStep("ConfirmMainScreen");
            renderNewStep("NewRegister");
            resolve("Success");
        });
    const handleMainScreen = () => {
        saveOnGa({
            event: "login_event",
            category: "Account",
            action: "Account",
            label: "action :: change email"
        });
        setUser({
            ...user,
            email: ""
        });
        renderNewStep("MainScreen");
    };

    return (
        <>
            <HeaderLogin isModal={isModal} previousStep={previousStep} onCloseClick={onCloseClick} />
            <div className={classes.root}>
                <Title className={classes.title}>{translate({ id: "ConfirmMainScreen.title" })}</Title>
                <SubTitle className={classes.subTitle}>
                    {translate({
                        id: "ConfirmMainScreen.subTitle",
                        value: {
                            email: <strong className={classes.email}>{user ? user.email : ""}</strong>,
                            brand: <span>da {ctx.name} </span>
                        }
                    })}
                </SubTitle>
                <Link onClick={handleMainScreen} className={classes.changeEmail}>
                    <SubTitle variant="caption" className={classes.subTitleLinkChangeEmail}>
                        {translate({ id: "ConfirmMainScreen.linkChangeEmail" })}
                    </SubTitle>
                </Link>
                <div className={classes.buttonContainer}>
                    <InternalButton type="button" text={intl.formatMessage({ id: "ConfirmMainScreen.createAccountButton" })} handlerClick={handleCreateAccount} />
                </div>
            </div>
            <Terms />
        </>
    );
};

export default injectIntl(ConfirmMainScreen);
