import React, { useContext } from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Grid from "@material-ui/core/Grid";
import Typography from "../Typography";
import Container from "../Container";
import Logo from "../Logo";
import styles from "./styles";
import { BrandContext } from "../BrandContext";
import { isWebView } from "../WebView";
import { v4 as uuidv4 } from "uuid";


const Footer = ({ classes, className }) => {
    if (isWebView)
        return null;

    const ctx = useContext(BrandContext);

    if (ctx == null) {
        throw { message: "BrandContext not found. please import BrandContextProvider in your root App." };
    }

    const {
        footer: { sections, apps, socialNetworks, payments, texts, appTitle, socialNetworksTitle }
    } = ctx;


    return (
        <footer className={`${className} ${classes.root}`}>
            <Container>
                <Grid container>
                    <Grid item xs={10} lg={11} className={classes.main}>
                        <Grid container spacing={9}>
                            {sections && sections.map(section => renderSection(classes, section, uuidv4()))}
                        </Grid>
                    </Grid>
                    <Grid item xs={2} lg={1}>
                        <h2 className={classes.logoContainer}>
                            <a href="/">
                                <Logo type="dark" />
                            </a>
                        </h2>
                    </Grid>
                    <Grid item xs={12}>
                        <Grid container>
                            <Grid item xs={12} lg={4}>
                                <h2 className={classes.title}>
                                    <Typography type="headline6">{appTitle}</Typography>
                                </h2>
                                <ul className={classes.list}>
                                    {apps &&
                                        apps.map(app => (
                                            <li className={classes.listItem} key={uuidv4()}>
                                                <a href={app.url}>
                                                    <img src={app.image} alt={app.name} />{" "}
                                                </a>
                                            </li>
                                        ))}
                                </ul>
                            </Grid>
                            <Grid item xs={12} lg={4}>
                                <h2 className={classes.title}>
                                    <Typography type="headline6">{socialNetworksTitle}</Typography>
                                </h2>
                                <div className={classes.iconContainer}>{socialNetworks && socialNetworks.map(sn => renderSocialNetwork(classes, sn, uuidv4()))}</div>
                            </Grid>
                        </Grid>
                    </Grid>
                    <Grid item xs={12} className={classes.paymentGrid}>
                        <Grid container>{payments && payments.map(payment => renderPayment(classes, payment, uuidv4()))}</Grid>
                    </Grid>
                    <Grid item xs={12} className={classes.footer}>
                        {texts && texts.map(text => renderFooterText(classes, text, uuidv4()))}
                    </Grid>
                </Grid>
            </Container>
        </footer>
    );
};

const renderSection = (classes, { title, items }, uuid) => (
    <Grid key={uuid} item xs={12} lg={3} className={classes.mainItems}>
        <h2 className={classes.title}>
            <Typography type="headline6">{title}</Typography>
        </h2>
        {items && items.map(sectionItem => renderSectionItem(classes, sectionItem, uuidv4()))}
    </Grid>
);

const renderSocialNetwork = (classes, { name, icon, attributes }, uuid) => (
    <a key={uuid} className={`${classes.icon} gtm-link-event`} {...attributes} rel="noopener noreferrer">
        <img src={icon} alt={name} />
    </a>
);

const renderSectionItem = (classes, { type, text, attributes }, uuid) => {
    const fontSize = `body${type.toUpperCase() === "PHONELINK" ? 1 : 2}regular`;
    const child = (
        <Typography className={type.toUpperCase() === "TEXT" && classes.links} type={fontSize}>
            {text}
        </Typography>
    );
    if (type.toUpperCase() === "TEXT") return child;
    return (
        <a key={uuid} className={`${classes.links} ${type.toUpperCase() === "GTMLINK" && "gtm-link-event"}`} {...attributes}>
            {child}
        </a>
    );
};

const renderPayment = (classes, { title, flags }, uuid) => (
    <Grid item xs={4} key={uuid}>
        <h2 className={classes.title}>
            <Typography type="headline6">{title}</Typography>
        </h2>
        <ul className={classes.list}>
            {flags.map(({ icon, name }) => (
                <li className={classes.listItem} key={uuidv4()}>
                    <img src={icon} alt={name} />
                </li>
            ))}
        </ul>
    </Grid>
);

const renderFooterText = (classes, { text, textOnBreakMobile }, uuid) => (
    <Typography className={classes.footerText} type="body2regular" key={uuid}>
        {`${text} `}
        <span className={classes.breakMobile}>{textOnBreakMobile}</span>
    </Typography>
);

Footer.propTypes = { classes: PropTypes.object.isRequired };
Footer.defaultProps = {};

export default withStyles(styles, { withTheme: true })(Footer);
