import { Amplify } from "aws-amplify";
import { getHostName } from "../../BrandContext/BrandContextProvider";

export default function configAws(config, hosts) {
    return Amplify.configure(setconfigAmplify(config, hosts));
}

function setconfigAmplify({ region, userPoolId, userPoolWebClientId, domain }: configAmplifyAws, hosts) {
    return {
        Auth: {
            userPoolId,
            userPoolWebClientId,
            aws_project_region: region,
            aws_cognito_region: region,
            aws_user_pools_id: "us-east-1_7jpjvLoHD",
            authenticationFlowType: "USER_PASSWORD_AUTH",
            aws_user_pools_web_client_id: "7occ4e2u0rn0t6hp3apoa7bibm",
            oauth: {
                domain,
                scope: [
                    "email",
                    "openid",
                    "profile",
                ],
                redirectSignIn: `${Redirect(hosts)}/mytravels/reservations/`,
                redirectSignOut: `${Redirect(hosts)}/`,
                responseType: "token"
            },
            federationTarget: "COGNITO_USER_POOLS"
        },
    };
}

export interface configAmplifyAws {
    region: string;
    userPoolId: string;
    userPoolWebClientId: string;
    domain: string;
}

export function Redirect(hosts?: Array<string>): string | undefined {
    const urlLocal = hosts ? `https://${hosts.find((h) => h.includes("ti"))}` : "https://ti.submarinoviagens.com.br"

    const urlToRedirect = getHostName().hostname.includes("local")
        ? urlLocal
        : `https://${getHostName().hostname}`;

    // eslint-disable-next-line no-console
    console.info("URL DE REDIRECIONAMENTO AO SAIR", urlToRedirect);
    return urlToRedirect;
}
