/* eslint-disable react/no-array-index-key */
/* eslint-disable no-unused-expressions */
/* eslint-disable radix */
/* eslint-disable no-nested-ternary */
import React from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Grid from "@material-ui/core/Grid";
import Luggage from "@cvccorp-components/icons/dist/Luggage";
import Help from "@cvccorp-components/icons/dist/Help";
import Typography from "../Typography";
import Button from "../Button";
import LoadingContent from "../LoadingContent";
import AirportTag from "../AirportTag";
import Stops from "../Stops";
import Box from "../Box";
import { styles } from "./styles";

const FlightCard = ({ classes, onClick, legs, isLoading, origin, destiny, pax, onChangeFlights }) => {
    const renderLoadingCard = () => {
        return (
            <Grid container>
                <Grid item xs={12} lg={9}>
                    <ul className={classes.legContainer}>
                        {legs.map((item, i) => {
                            const highlightIata = legs[0].departureIata === legs[legs.length - 1].arrivalIata;
                            return (
                                <li className={classes.leg} key={i}>
                                    <Grid container>
                                        <Grid item xs={12} lg={6}>
                                            <header className={classes.header}>
                                                <Grid item xs={2}>
                                                    <LoadingContent />{" "}
                                                </Grid>
                                                <Grid item xs={10}>
                                                    <Grid item xs={3}>
                                                        <LoadingContent />
                                                    </Grid>
                                                    <Grid item xs={7}>
                                                        <LoadingContent />
                                                    </Grid>
                                                </Grid>
                                            </header>
                                        </Grid>
                                        <Grid item xs={12} lg={6}>
                                            <Grid container>
                                                <Grid item xs={10}>
                                                    <Grid container>
                                                        <Grid item xs={2}>
                                                            <LoadingContent />
                                                            {highlightIata ? <LoadingContent /> : <LoadingContent />}
                                                        </Grid>
                                                        <Grid className={classes.stops} item xs={5} align="center">
                                                            <LoadingContent />
                                                            <LoadingContent />
                                                        </Grid>
                                                        <Grid item xs={2}>
                                                            <LoadingContent />
                                                            <LoadingContent />
                                                        </Grid>
                                                    </Grid>
                                                </Grid>
                                                <Grid item xs={2}>
                                                    {item.luggage ? <LoadingContent /> : <LoadingContent />}
                                                </Grid>
                                            </Grid>
                                        </Grid>
                                        <Grid item xs={12} lg={10}>
                                            <div className={classes.legFooter}>
                                                <Grid item xs={3}>
                                                    <LoadingContent />
                                                </Grid>
                                                <Grid item xs={2}>
                                                    <LoadingContent />
                                                </Grid>
                                            </div>
                                        </Grid>
                                    </Grid>
                                </li>
                            );
                        })}
                    </ul>
                </Grid>
                <Grid className={classes.price} item xs={12} lg={3}>
                    <Grid container alignItems="flex-end">
                        <Grid item xs={6} lg={12}>
                            <Grid item xs={5}>
                                <LoadingContent />
                            </Grid>

                            <Grid item xs={3}>
                                <LoadingContent />
                            </Grid>

                            <Grid item xs={4}>
                                <LoadingContent />
                            </Grid>
                            <Grid item xs={4}>
                                <LoadingContent />
                            </Grid>

                            <br />
                        </Grid>

                        <Grid item xs={6} lg={12} align="center">
                            <Grid item xs={12}>
                                <LoadingContent />
                            </Grid>
                            <Grid item xs={8}>
                                <LoadingContent />
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        );
    };

    const renderCardHeader = (item, i, name) => {
        return (
            <header className={classes.header}>
                <img className={classes.ciaLogo} src={`https://almundo-com-res.cloudinary.com/image/upload/global/logos/digital-air-companies/${item && item.issuerCia}.svg`} alt={item.cia} />
                <div>
                    <Typography type="headline6">{name}</Typography>{" "}
                    <Typography className={classes.cia} type="body2regular">
                        ({item.cia})
                    </Typography>
                    <Typography className={classes.date} type="body2regular">
                        {item.date} - {item.class}
                    </Typography>
                </div>
            </header>
        );
    };

    const renderCardTime = (item, highlightIata) => {
        return (
            <Grid container justifyContent="center">
                <Grid item>
                    <Typography className={classes.airport} type="headline4">
                        {item.departure}
                    </Typography>
                    {/* {highlightIata ? (
            <AirportTag label={item.departureIata} color="white" />
          ) : (
            <AirportTag
              label={item.departureIata}
              icon={<Help />}
              color="spot"
            />
          )} */}
                    <AirportTag label={item.departureIata} color="white" />
                </Grid>
                <Grid className={classes.stops} item xs={5} lg={9} align="center">
                    <Typography type="captionregular">
                        {item.duration}
                        <Stops className={classes.stopsIcon} qtd={item.stops} />
                        {item.stops > 0 ? `${item.stops} parada${item.stops > 1 ? "s" : ""}` : "direto"}
                    </Typography>
                </Grid>
                <Grid item>
                    <Typography className={classes.airport} type="headline4">
                        {item.arrival}
                    </Typography>
                    <AirportTag label={item.arrivalIata} color="white" />
                </Grid>
            </Grid>
        );
    };

    const renderBaggageInfo = item => {
        return (
            <>
                {item && item.luggage && item.luggage.checkedBaggageQuantity > 0 && item.luggage.handBaggageQuantity > 0 && (
                    <Typography type="captionregular">
                        <Luggage className={classes.luggage} size="medium" carryOn handBag checkedBag />
                        Bagagens Inclusas
                    </Typography>
                )}
                {item && item.luggage && item.luggage.checkedBaggageQuantity === 0 && item.luggage.handBaggageQuantity > 0 && (
                    <Typography type="captionregular">
                        <Luggage className={classes.luggage} size="medium" carryOn handBag />
                        Bagagem de mo
                    </Typography>
                )}
                {item && item.luggage && item.luggage.checkedBaggageQuantity > 0 && item.luggage.handBaggageQuantity === 0 && (
                    <Typography type="captionregular">
                        <Luggage className={classes.luggage} size="medium" carryOn handBag />
                        Bagagem de mo
                    </Typography>
                )}
                {item && item.luggage && item.luggage.checkedBaggageQuantity === 0 && item.luggage.handBaggageQuantity === 0 && (
                    <Typography type="captionregular">
                        <Luggage className={classes.luggage} size="medium" carryOn />
                        Sem Bagagem
                    </Typography>
                )}
            </>
        );
    };

    return (
        <Box className={classes.root} onClick={onClick}>
            {!isLoading && (
                <Grid container>
                    <Grid item xs={12}>
                        <ul className={classes.legContainer}>
                            <Grid className={classes.title} item xs={12}>
                                <div>
                                    <Typography type="headline6">
                                        {origin} - {destiny}
                                    </Typography>
                                </div>
                                <div>
                                    <Typography type="body2" style={{ color: "#999999" }}>
                                        {pax} viajantes
                                    </Typography>
                                </div>
                            </Grid>
                            {legs.map((item, i) => {
                                const name = legs.length < 3 ? (i === 0 ? "Ida" : "Volta") : `Trecho ${i + 1}`;
                                const highlightIata = legs[0].departureIata === legs[legs.length - 1].arrivalIata;
                                return (
                                    <li className={classes.leg}>
                                        <Grid container className={classes.spacing}>
                                            <Grid item xs={12} sm={4}>
                                                {renderCardHeader(item, i, name)}
                                            </Grid>
                                            <Grid item xs={12} sm={6}>
                                                {renderCardTime(item, highlightIata)}
                                            </Grid>
                                            <Grid item xs={12} sm={2} className={classes.centerContent}>
                                                <Button className={classes.detailBtn} outline label={`Detalhes da ${name}`} variant="text" onClick={() => item.onDetail()} />
                                            </Grid>
                                            {name === "Volta" && (
                                                <Grid xs={12} lg={12} item container alignItems="center" className={classes.paddingTop24}>
                                                    <Grid container item lg={6} xs={12}>
                                                        <div className={classes.legFooter}>{renderBaggageInfo(item)}</div>
                                                    </Grid>
                                                    <Grid container item xs={12} lg={name === "Volta" ? 6 : 12} justify="flex-end">
                                                        <Button className={classes.btnChangeFlight} color="secondary" label="Alterar voo" variant="contained" onClick={() => onChangeFlights()} />
                                                    </Grid>
                                                </Grid>
                                            )}
                                        </Grid>
                                    </li>
                                );
                            })}
                        </ul>
                    </Grid>
                </Grid>
            )}
            {isLoading && renderLoadingCard()}
        </Box>
    );
};

FlightCard.propTypes = {
    classes: PropTypes.object.isRequired,
    legs: PropTypes.array.isRequired,
    onClick: PropTypes.func,
    isLoading: PropTypes.bool,
    origin: PropTypes.string,
    destiny: PropTypes.string
};

export default withStyles(styles, { withTheme: true })(FlightCard);
