import React, { useState } from "react";
import useStyles from "./styles";
import { Button as MaterialButton, CircularProgress } from "@material-ui/core";
import Button from "../../../../Button";

interface ButtonProps {
    text: any;
    type?: string;
    isSocialNetwork?: boolean;
    icon?: React.FC;
    handlerClick?: () => Promise<any> | undefined | void;
}

const InternalButton: React.FC<ButtonProps> = ({
    text,
    isSocialNetwork,
    type,
    icon: Icon,
    handlerClick,
}: ButtonProps) => {
    const classes = useStyles({});
    const [isLoading, setIsLoading] = useState(false);
    const handlerOnClick = async () => {
        setIsLoading(true);
        if (handlerClick) {
            await handlerClick()
        }
        setIsLoading(false);
    };

    if (isSocialNetwork && Icon) {
        return (
            <div>
                <Button
                    className={classes.socialButton}
                    label={isLoading ? "" : text}
                    size="medium"
                    variant="outlined"
                    icon={isLoading ? <CircularProgress size={24} color="primary" /> : <Icon />}
                    iconPosition="before"
                    fullWidth
                    onClick={handlerOnClick}
                />
            </div>
        );
    } else {
        return (
            <MaterialButton
                className={classes.btnLogin}
                size="medium"
                color="primary"
                variant="contained"
                fullWidth
                type={type || "button"}
                href=""
                onClick={handlerOnClick}
            >{isLoading ? <CircularProgress size={24} color="inherit" /> : text}</MaterialButton>
        );

    }
};

export default InternalButton;
