import React, { useContext, useEffect, useMemo, useState } from "react";
import Dropdown from "@cvccorp-components/icons/dist/DropDown";
import Help from "@cvccorp-components/icons/dist/Help";
import * as IconsDist from "@cvccorp-components/icons/dist/index";
import Sac from "@cvccorp-components/icons/dist/Sac";
import Store from "@cvccorp-components/icons/dist/Store";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import PropTypes from "prop-types";
import { BrandContext } from "../BrandContext";
import { Brand } from "../BrandContext/BrandContext";
import Container from "../Container";
import DropDown from "../DropDown";
import Logo from "../Logo";
import MenuMobile from "../MenuMobile";
import Typography from "../Typography";
import UserArea from "../UserArea";
import { HeaderHideBgContext } from "./HeaderBlock";
import { styles } from "./styles";

function buildIcon(icon, props) {
    if (icon == null) return null;
    if (icon.indexOf("http") === 0) return <img src={icon} {...props} style={{ verticalAlign: "middle" }} />;

    const Icon = IconsDist[icon];
    if (Icon == null) return null;
    return <Icon {...props} />;
}
const showHiddenMenu = typeof window !== "undefined" && new URLSearchParams(location.search).has("showHiddenMenu");
function getMenu(menuList, hiddenList) {
    if (!showHiddenMenu || !hiddenList || hiddenList.length === 0) return menuList;
    return [...menuList, ...hiddenList];
}
const useHeaderStyles = makeStyles(styles);

function FindAStore({ classes }) {
    return (
        <div className={classes.headline}>
            <a href="https://lojas.cvc.com.br/" style={{ textDecoration: "none" }} target="_blank" rel="noopener noreferrer">
                <Typography type="captionsemibold" className={classes.findAStore}>
                    <Store className={classes.icon} size="medium" />
                    Encontre uma loja CVC
                </Typography>
            </a>
        </div>
    );
}
function Header({ small, product, user, isMobile, menuSurrogateObject }) {
    const ctxHideBg = useContext(HeaderHideBgContext);
    const styleProps = useMemo(() => ({ hideDefaultBg: ctxHideBg }), [ctxHideBg]);
    const classes = useHeaderStyles(styleProps);
    const ctx = useContext(BrandContext);
    if (ctx == null) throw { message: "BrandContext not found. please import BrandContextProvider in your root App." };

    const isRenderMenuSurrogate = Boolean(menuSurrogateObject);
    // const isRenderUser = Boolean(isUser);
    const MObject = menuSurrogateObject ?? Menu;
    const {
        menu: { principals, hiddenPrincipals, others, hiddenOthers, phone, phoneSales, help }
    } = ctx;

    const selectedProduct = [...getMenu(principals, hiddenPrincipals), ...getMenu(others, hiddenOthers)].find(x => x.product === product);
    const renderBreadCrumb = () => {
        const renderListItem = (item, index) => (
            <li className="breadcrumb-item" itemProp="itemListElement" itemScope="" itemType="http://schema.org/ListItem" key={index}>
                <a itemProp="item" href={item.link} title={item.label} target={item.target}>
                    <span itemProp="name">{item.label}</span>
                </a>
                <meta itemProp="position" content={index + 1} />
            </li>
        );

        return (
            <div id="container-breadcrumbs" style={{ display: "none" }}>
                <ul itemProp="breadcrumb" className="breadcrumb-container" itemScope="" itemType="http://schema.org/BreadcrumbList">
                    {getMenu(principals, hiddenPrincipals).map((item, index) => renderListItem(item, index))}
                </ul>
            </div>
        );
    };

    const RenderMobile = () => {
        const {logo} = useContext(BrandContext);

        return (
            <div className={classes.mobile}>
                <a href="/">
                    <Logo type="light" className={classes.mobileLogo} style={{ width: logo.preferredWidthMobile }} />
                </a>
                <MObject type="Mobile" classes={classes} selectedProduct={selectedProduct} user={user} isMobile={isMobile} />
            </div>
        );
    };

    const RenderDesktop = () => {
        const {logo} = useContext(BrandContext);

        return (
            <>
                <div className={classes.top}>
                    <Container>
                        {ctx.brand === Brand.CVC && <FindAStore classes={classes} />}
                        {phone && (
                            <div className={classes.headline}>
                                <Typography type="captionregular" className={classes.contact}>
                                    <Sac className={classes.icon} size="medium" />
                                    {!phoneSales && "Televendas e"} Central de atendimento:
                                    <Typography type="captionsemibold" className={classes.white}>
                                        {phone}
                                    </Typography>
                                </Typography>
                            </div>
                        )}
                        {phoneSales && (
                            <div className={classes.headline}>
                                <Typography type="captionregular" className={classes.contact}>
                                    <span className={classes.televendas}>
                                        Televendas:
                                        <Typography type="captionsemibold" className={classes.white}>
                                            {phoneSales}
                                        </Typography>
                                    </span>
                                </Typography>
                            </div>
                        )}

                        {help && (
                            <Typography type="body2regular" className={classes.help}>
                                <a className={classes.helpLink} href={help}>
                                    <Help className={classes.icon} size="medium" />
                                    Precisa de ajuda?
                                </a>
                            </Typography>
                        )}
                    </Container>
                </div>
                <div className={small ? classes.mainSmall : classes.main}>
                    <Container>
                        <Grid container>
                            <Grid item xs={small ? 9 : 1} className={classes.logoContainer}>
                                <a href="/">
                                    <Logo type="light" className={classes.logo} style={{ width: logo.preferredWidth }} />
                                </a>
                                {small && <MObject type="DeskSmall" classes={classes} selectedProduct={selectedProduct} user={user} isMobile={isMobile} />}
                            </Grid>
                            {!small && <MObject type="DeskRegular" classes={classes} selectedProduct={selectedProduct} user={user} isMobile={isMobile} />}
                            {!isRenderMenuSurrogate && (
                                <Grid item xs={3}>
                                    <UserArea />
                                </Grid>
                            )}
                        </Grid>
                    </Container>
                </div>
            </>
        );
    };

    return (
        <>
            <header id="header">{isMobile ? RenderMobile() : RenderDesktop()}</header>
            {renderBreadCrumb()}
        </>
    );
}

function MenuMob({ classes, selectedProduct, user, isMobile }) {
    const ctx = useContext(BrandContext);
    var ctxMenu = ctx.menu;
    const { principals, hiddenPrincipals, others, hiddenOthers, phone, phoneSales, help } = ctxMenu;
    const [isOpen, setIsOpen] = useState(false);

    const princ = getMenu(principals, hiddenPrincipals);
    const ot = getMenu(others, hiddenOthers);

    useEffect(() => {
        if (isOpen && typeof window !== "undefined" && window.document && isMobile) {
            window.document.body.classList.add("stop-scroll");
            return () => window.document.body.classList.remove("stop-scroll");
        }
    }, [isOpen]);

    const customPrincipals = useMemo(() => {
        if (ctx?.brand !== Brand.CVC || !princ) return princ;
        return [
            ...princ,
            {
                icon: "Store",
                label: "Encontre uma loja CVC",
                link: "https://lojas.cvc.com.br/",
                target: "_blank"
            }
        ];
    }, [ctx?.brand, principals, hiddenPrincipals]);

    return (
        <>
            <a aria-controls="simple-menu" aria-haspopup="true" onClick={() => setIsOpen(true)}>
                <Typography type="body2regular" className={classes.menuMobile}>
                    {selectedProduct?.label}
                    <Dropdown />
                </Typography>
            </a>
            <MenuMobile
                isOpen={isOpen}
                goBack={() => setIsOpen(false)}
                user={user}
                product={selectedProduct?.label}
                more={ot.length > 0}
                help={help}
                phone={phone}
                phoneSales={phoneSales}
                menu={customPrincipals.map(x => ({ ...x, icon: buildIcon(x.icon) }))}
                submenu={ot.map(x => ({ ...x, icon: buildIcon(x.icon) }))}
                ctx={ctx}
            />
        </>
    );
}

function MenuDeskSmall({ selectedProduct, classes }) {
    var ctxMenu = useContext(BrandContext).menu;
    const { principals, hiddenPrincipals, others, hiddenOthers } = ctxMenu;

    const [isMoreActive, setIsIsMoreActive] = useState(false);
    const [isOpen, setIsOpen] = useState(false);

    const ot = getMenu(others, hiddenOthers);
    return (
        <>
            <button className={classes.productBtn} onClick={() => setIsOpen(true)}>
                <Typography type="body1regular">
                    {buildIcon(selectedProduct?.icon, { className: classes.productBtnIcon })} {selectedProduct?.label} <Dropdown />
                </Typography>
            </button>
            {isOpen && (
                <>
                    <span onClick={() => setIsOpen(false)} className={classes.overlay} />
                    <div className={classes.submenuSmall}>
                        {!isMoreActive && (
                            <ul className={clsx(classes.submenuSmallList, isMoreActive ? classes.mainMenuInactive : classes.mainMenuActive)}>
                                {getMenu(principals, hiddenPrincipals).map((item, key) => (
                                    <li key={key} className={classes.submenuSmallItem}>
                                        <a href={item.link} target={item.target} title={item.label} alt={item.label} className={item.active ? clsx(classes.menuSmall, classes.menuSmallActive) : classes.menuSmall}>
                                            <Typography type="body1regular">
                                                <span className={classes.menuSmallIcon}>{buildIcon(item.icon)}</span>
                                                {item.label}
                                            </Typography>
                                        </a>
                                    </li>
                                ))}
                            </ul>
                        )}
                        {ot.length > 0 && (
                            <Typography type="headline6" onClick={() => setIsIsMoreActive(!isMoreActive)} className={clsx(classes.submenuSmallTitle, isMoreActive ? classes.submenuSmallTitleActive : classes.submenuSmallTitleInactive)}>
                                Mais
                                <Dropdown className={classes.submenuSmallTitleIcon} />
                            </Typography>
                        )}
                        <ul className={clsx(classes.submenuSmallList, isMoreActive ? classes.submenuMoreActive : classes.submenuMoreInactive)}>
                            {ot.map((item, key) => (
                                <li key={key} className={classes.submenuSmallItem}>
                                    <a href={item.link} target={item.target} title={item.label} alt={item.label} className={classes.menuSmall}>
                                        <Typography type="body1regular">
                                            <span className={classes.menuSmallIcon}>{buildIcon(item.icon)}</span>
                                            {item.label}
                                        </Typography>
                                    </a>
                                </li>
                            ))}
                        </ul>
                    </div>
                </>
            )}
        </>
    );
}

function MoreItemsOpened({ others, classes }) {
    const fullmenu = useMemo(() => {
        const items = others.map((item, key) => (
            <a key={key + 1} href={item.link} target={item.target} title={item.label} alt={item.label} className={classes.submenuItem}>
                <span className={classes.icon}>{buildIcon(item.icon)}</span> {item.label}
            </a>
        ));

        return [
            <Typography key={0} type="headline5" className={classes.submenuItem}>
                Mais
            </Typography>,
            ...items
        ];
    }, [others]);

    return <DropDown className={classes.submenu} menu={fullmenu} />;
}

function MoreItems({ others, classes }) {
    const [itemSelected, setItemSelected] = useState(undefined);

    return (
        <li className={classes.menu}>
            <span className={classes.menuLink} onClick={() => setItemSelected(itemSelected !== others ? others : undefined)}>
                <Typography type="body1regular">Mais</Typography>
                <Dropdown />
            </span>
            {itemSelected && (
                <>
                    <span className={classes.overlay} onClick={() => setItemSelected(undefined)} />
                    <MoreItemsOpened others={others} classes={classes} />
                </>
            )}
        </li>
    );
}
function ItemMenu({ selectedProduct, classes, menu }) {
    return (
        <li className={classes.menu}>
            <a id={menu.id} href={menu.link} target={menu.target} title={menu.label} alt={menu.label} className={classes.menuLink}>
                <Typography type={menu.label === selectedProduct?.label ? "body1semibold" : "body1regular"} className={clsx(classes.menuText, menu.label === selectedProduct?.label && classes.active)}>
                    <span className={classes.icon}>{buildIcon(menu.icon)}</span>
                    {menu.label}
                </Typography>
            </a>
        </li>
    );
}

function MenuDeskRegular({ selectedProduct, classes }) {
    var ctxMenu = useContext(BrandContext).menu;
    const { principals, hiddenPrincipals, others, hiddenOthers } = ctxMenu;

    const ot = getMenu(others, hiddenOthers);
    return (
        <Grid item xs={8}>
            <ul className={classes.menuList}>
                {getMenu(principals, hiddenPrincipals).map(item => (
                    <ItemMenu key={item.link} selectedProduct={selectedProduct} menu={item} classes={classes} />
                ))}
                {ot.length > 0 && <MoreItems others={ot} classes={classes} />}
            </ul>
        </Grid>
    );
}

const dictMenuType = {
    DeskRegular: MenuDeskRegular,
    DeskSmall: MenuDeskSmall,
    Mobile: MenuMob
};
function Menu({ type, classes, user, selectedProduct, isMobile }) {
    const T = dictMenuType[type];
    return <T selectedProduct={selectedProduct} classes={classes} user={user} isMobile={isMobile} />;
}

Header.propTypes = {
    user: PropTypes.object,
    isMobile: PropTypes.bool,
    small: PropTypes.bool,
    product: PropTypes.string,
    menuSurrogateObject: PropTypes.object
};

export default Header;
