import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Grid from "@material-ui/core/Grid";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import { Calendar, User, Air, Delete, Plus } from "@cvccorp-components/icons";
import Typography from "../Typography";
import Button from "../Button";
import PesqincAir from "../Pesqinc/Air";
import FullCalendar from "../Calendar/FullCalendar";
import PaxAir from "../Pax/Air";
import CustomChip from "../CustomChip";
import Tabs from "../Tabs";
import Container from "../Container";
import BG from "../assets/img/bg-motor.svg";

const styles = theme => ({
    search: {
        background: theme.palette.primary.main,
        padding: `${theme.spacing(3)}px ${theme.spacing(3)}px`,
        [theme.breakpoints.between("md", "xl")]: {
            background: `url(${BG}) no-repeat ${theme.palette.primary.main} center bottom 40px`,
            padding: `${theme.spacing(5)}px ${theme.spacing(4)}px ${theme.spacing(
                25
            )}px`,
        },
    },
    research: {
        background: theme.palette.primary.main,
        padding: `${theme.spacing(3)}px ${theme.spacing(3)}px`,
        [theme.breakpoints.between("md", "xl")]: {
            padding: `${theme.spacing(2)}px ${theme.spacing(3)}px`,
        },
    },
    title: { color: theme.palette.white.main },
    btn: {
        margin: `${theme.spacing(1)}px 0`,
        width: "100%",
    },
    container: {
        marginLeft: theme.spacing(-1),
        marginTop: theme.spacing(3),
        width: `calc(100% + ${theme.spacing(2)}px)`,
    },
    researchContainer: {
        marginLeft: theme.spacing(-1),
        width: `calc(100% + ${theme.spacing(2)}px)`,
    },
    grid: {
        [theme.breakpoints.between("md", "xl")]: {
            padding: `0 ${theme.spacing(1)}px`,
        },
    },
    radioGroup: {
        flexDirection: "row",
        padding: `0 ${theme.spacing(1)}px`,
    },
    formControl: { marginRight: theme.spacing(3) },
    radioLabel: { color: theme.palette.white.main },
    origin: { position: "relative" },
    icon: {
        background: theme.palette.primary.main,
        borderRadius: "100%",
        color: theme.palette.white.main,
        left: "50%",
        position: "absolute",
        top: "50%",
        transform: "translate(-50%, -50%)",
        zIndex: 2,
    },
    originField: {
        paddingRight: theme.spacing(0.5),
        position: "relative",
        input: {
            "&::placeholder": {
                textOverflow: "ellipsis !important",
                color: "white",
            },
        },
    },
    destinyField: {
        paddingLeft: theme.spacing(0.5),
        position: "relative",
    },
    labelOrigin: {
        left: "14px",
        position: "absolute",
        top: "13px",
    },
    labelDestiny: {
        right: "14px",
        position: "absolute",
        top: "13px",
    },
    labelWhite: { color: theme.palette.white.main },
    multidestinationsArea: { padding: theme.spacing(1) },
    leg: { "& + &": { marginLeft: theme.spacing(1) } },
    addLeg: { margin: "5px 0" },
    researchOriginContainer: { display: "inline-block" },
    researchOrigin: {
        display: "flex",
        flexDirection: "column",
    },
    researchDestinyContainer: { display: "inline-block" },
    researchDestiny: {
        display: "flex",
        flexDirection: "column",
    },
    researchOriginDestinyContainer: {
        boxSizing: "border-box",
        display: "inline-block",
        outline: "none",
        minWidth: "250px",
    },
    researchOriginDestinyContainerFirst: {
        paddingRight: theme.spacing(4),
        position: "relative",
    },
    researchOriginDestinyContainerSecond: {
        padding: `0 ${theme.spacing(4)}px`,
        position: "relative",
        [theme.breakpoints.between("md", "xl")]: {
            "&:before": {
                content: "\"...\"",
                color: theme.palette.white.main,
                fontSize: "50px",
                left: "-15px",
                lineHeight: "6px",
                position: "absolute",
                top: "5px",
            },
        },
    },
    researchIcon: {
        color: theme.palette.white.main,
        left: "-15px",
        position: "absolute",
        top: "5px",
        [theme.breakpoints.between("md", "xl")]: { display: "none" },
    },
    researchOriginDestiny: {
        display: "flex",
        justifyContent: "space-between",
        position: "relative",
        [theme.breakpoints.between("xs", "md")]: { marginBottom: theme.spacing(3) },
    },
    researchOriginDestinyIcon: {
        color: theme.palette.white.main,
        left: "50%",
        position: "absolute",
        transform: "translate(-50%,-50%)",
        top: "50%",
    },
    text: { color: theme.palette.white.main },
    researchInfo: {
        display: "inline-block",
        [theme.breakpoints.between("xs", "md")]: { width: "calc(100% - 121px)" },
        [theme.breakpoints.between("md", "xl")]: {
            borderLeft: `1px solid ${theme.palette.white.alpha}`,
            margin: `0 ${theme.spacing(2)}px`,
            paddingLeft: theme.spacing(2),
        },
    },
    researchPax: {
        [theme.breakpoints.between("xs", "md")]: {
            borderLeft: `1px solid ${theme.palette.white.alpha}`,
            marginLeft: theme.spacing(1),
        },
    },
    researchBtn: {
        [theme.breakpoints.between("xs", "md")]: {
            border: "none",
            paddingRight: 0,
        },
    },
    researchMultidestination: {
        [theme.breakpoints.between("md", "xl")]: {
            display: "inline-block",
            maxWidth: "50%",
            verticalAlign: "middle",
        },
    },
});

const mockHistory = [
    {
        name: "Arraial do Cabo",
        arrival: "06 de jun",
        departure: "09 de jun",
    },
    {
        name: "FlorianÃ³polis",
        arrival: "26 de mar",
        departure: " 28 de mar",
    },
];

const mockMostSearched = [
    { name: "Lisboa", price: "178" },
    { name: "Arraial do Cabo", price: "93" },
    { name: "Buenos Aires", price: "109" },
    { name: "Santiago", price: "143" },
    { name: "Porto de Galinhas", price: "143" },
];

let timerPesqinc = null;

const sortFunc = (a, b) => {
    if (a.type !== b.type && a.type === "location") return -1;
    if (a.type !== b.type && b.type === "air") return 1;
    return 0;
};

const SearchAir = props => {
    const { classes } = props;
    const [destiny, setDestiny] = useState("");
    const [origin, setOrigin] = useState("");
    const [originItem, setOriginItem] = useState("");
    const [destinyItem, setDestinyItem] = useState("");
    const [search, setSearch] = useState([]);
    const [groupedDestinations, setGroupedDestinations] = useState(new Map());
    const [historySearch, setHistorySearch] = useState([]);
    const [mostSearched, setMostSearched] = useState([]);

    const initialState = {
        apply: "Selecione os passageiros",
        adults: 1,
        children: 0,
        babies: 0,
        classes: "eco",
        directFlight: false,
    };

    const [open, setOpen] = useState(false);
    const [openPax, setOpenPax] = useState(false);
    const [fields, setFields] = useState(initialState);

    const handleOpen = () => {
        setOpen(!open);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleOpenPax = () => {
        setOpenPax(!openPax);
    };

    const handleClosePax = () => {
        setOpenPax(false);
    };

    const handleAdd = field => {
        const values = { ...fields };
        values[field] = values[field] + 1;
        setFields(values);
    };

    const handleRemove = field => {
        const values = { ...fields };
        values[field] = values[field] - 1;
        setFields(values);
    };

    const handleClasses = field => {
        const values = { ...fields };
        values.classes = field;
        setFields(values);
    };
    const handleApply = field => {
        const values = { ...fields };
        values.apply = field;
        setFields(values);
    };
    const handleDirectFlight = () => {
        const values = { ...fields };
        values.directFlight = !values.directFlight;
        setFields(values);
    };

    const handleClear = () => {
        setFields(initialState);
    };

    const leg = (item, i) => (
        <span
            key={i}
            className={`
        ${classes.researchOriginDestinyContainer}
        ${
        i === 0
            ? classes.researchOriginDestinyContainerFirst
            : i
                ? classes.researchOriginDestinyContainerSecond
                : ""
        }
        `}
        >
            {i > 0 && (
                <span className={classes.researchIcon}>
                    <Air size="large" />
                </span>
            )}
            <span className={classes.researchOriginDestiny}>
                <span className={classes.researchOriginDestinyIcon}>
                    <Air size="large" />
                </span>
                <span className={classes.researchOriginContainer}>
                    <span className={classes.researchOrigin}>
                        <Typography
                            type={props.isMobile ? "headline5" : "headline2"}
                            className={classes.text}
                        >
                            {item.originIata}
                        </Typography>
                        <Typography type="body2regular" className={classes.text}>
                            {item.origin}
                        </Typography>
                    </span>
                </span>
                <span className={classes.researchDestinyContainer}>
                    <span className={classes.researchDestiny}>
                        <Typography
                            type={props.isMobile ? "headline5" : "headline2"}
                            className={classes.text}
                        >
                            {item.destinyIata}
                        </Typography>
                        <Typography type="body2regular" className={classes.text}>
                            {item.destiny}
                        </Typography>
                    </span>
                </span>
            </span>
        </span>
    );

    const getMostSearched = () => mockMostSearched;

    const getHistory = () => mockHistory;

    const getLocations = async text => {
        if (!text) return [];
        const searchText = text.replace(/ /g, "+");
        const payload = await fetch(
            `https://euhghjmvi6.execute-api.us-east-1.amazonaws.com/b2c_ti/locations?productType=AIR&q=${searchText}`
        );
        const result = await payload.json();
        return result.locations
            .filter(item => !!item.IATA)
            .map((item, i) => ({
                ...item,
                key: { i },
                name: `${item.city}, ${item.state}`,
                address: item.description,
                type: item.type === "CITY" ? "location" : item.type.toLowerCase(),
            }));
    };

    useEffect(() => {
        setHistorySearch(getHistory);
        setMostSearched(getMostSearched);
    }, []);

    const groupBy = function groupBy(list, keyGetter) {
        const map = new Map();
        list.forEach((item) => {
            const key = keyGetter(item);
            const collection = map.get(key);
            if (!collection) {
                map.set(key, [item]);
            } else {
                collection.push(item);
            }
        });
        return map;
    };

    const changeText = text => {
        if (text && text.length < 3) return;
        if (timerPesqinc) clearTimeout(timerPesqinc);
        timerPesqinc = setTimeout(async () => {
            const result = await getLocations(text);
            const sortedResults = result.sort(sortFunc);
            const groupedDest = await groupBy(sortedResults, flight => flight.city);
            setGroupedDestinations(groupedDest);

            setSearch(sortedResults);
        }, 500);
    };

    const handlePesqinc = (e, tag) => {
        changeText(e.target.value);
        if (tag === "origin") setOrigin(e.target.value);
        else if (tag === "destiny") setDestiny(e.target.value);
    };

    const handleSelect = (item, tag) => {
        if (tag === "origin") {
            setOrigin(item.name);
            setOriginItem(item);
        } else if (tag === "destiny") {
            setDestiny(item.name);
            setDestinyItem(item);
        }
    };

    const handleReset = (item, tag) => {
        if (tag === "origin") {
            setOrigin("");
            setOriginItem(item);
        } else if (tag === "destiny") {
            setDestiny("");
            setDestinyItem(item);
        }
        changeText("");
    };

    return (
        <div className={props.research ? classes.research : classes.search}>
            <Container>
                {!props.research && (
                    <Grid container>
                        <Grid item lg={5}>
                            <Typography
                                type={props.isMobile ? "headline5" : "headline2"}
                                className={classes.title}
                            >
                OlÃ¡, encontre aqui os melhores voos para usa viagem!
                            </Typography>
                        </Grid>
                    </Grid>
                )}
                {(props.researchActive || !props.research) && (
                    <Grid
                        container
                        className={
                            props.research ? classes.researchContainer : classes.container
                        }
                    >
                        <Grid item xs={12}>
                            {!props.isMobile && (
                                <RadioGroup name="type" className={classes.radioGroup}>
                                    <FormControlLabel
                                        value="ida"
                                        control={
                                            <Radio
                                                color="secondary"
                                                classes={{ root: classes.radioLabel }}
                                                checked={props.type === "roundtrip"}
                                            />
                                        }
                                        label="Ida e volta"
                                        classes={{
                                            label: classes.radioLabel,
                                            root: classes.formControl,
                                        }}
                                    />
                                    <FormControlLabel
                                        value="somenteIda"
                                        control={
                                            <Radio
                                                color="secondary"
                                                classes={{ root: classes.radioLabel }}
                                                checked={props.type === "oneway"}
                                            />
                                        }
                                        label="Somente ida"
                                        classes={{ label: classes.radioLabel }}
                                    />
                                    <FormControlLabel
                                        value="multidestinos"
                                        control={
                                            <Radio
                                                color="secondary"
                                                classes={{ root: classes.radioLabel }}
                                                checked={props.type === "multidestinations"}
                                            />
                                        }
                                        label="Multidestinos"
                                        classes={{ label: classes.radioLabel }}
                                    />
                                </RadioGroup>
                            )}
                            {props.isMobile && (
                                <Tabs
                                    content={[
                                        { label: "Ida e volta" },
                                        { label: "Somente ida" },
                                        { label: "Multidestinos" },
                                    ]}
                                />
                            )}
                        </Grid>
                        {props.type === "multidestinations" && (
                            <Grid item xs={12}>
                                <Grid container className={classes.multidestinationsArea}>
                                    <Grid item lg={11} xs={11}>
                                        {props.destination.map((item, i) => {
                                            if (item.isActive) {
                                                if (item.originIata) {
                                                    if (props.destination.length > 2) {
                                                        return (
                                                            <CustomChip
                                                                className={classes.leg}
                                                                key={i}
                                                                label={`${item.originIata} - ${item.destinyIata}`}
                                                                icon={<Delete />}
                                                                onClick={() => alert("vai para trecho")}
                                                                iconClick={() => alert("deleta trecho")}
                                                            />
                                                        );
                                                    }
                                                    return (
                                                        <CustomChip
                                                            className={classes.leg}
                                                            key={i}
                                                            label={`${item.originIata} - ${item.destinyIata}`}
                                                            onClick={() => alert("vai para trecho")}
                                                        />
                                                    );
                                                }
                                                return (
                                                    <CustomChip
                                                        className={classes.leg}
                                                        key={i}
                                                        label={`Trecho ${i + 1}`}
                                                        onClick={() => alert("vai para trecho")}
                                                    />
                                                );
                                            }
                                            if (item.originIata) {
                                                return (
                                                    <CustomChip
                                                        className={classes.leg}
                                                        key={i}
                                                        label={`${item.originIata} - ${item.destinyIata}`}
                                                        color="primary"
                                                        onClick={() => alert("vai para trecho")}
                                                    />
                                                );
                                            }
                                            return (
                                                <CustomChip
                                                    className={classes.leg}
                                                    key={i}
                                                    label={`Trecho ${i + 1}`}
                                                    variant="outlined"
                                                    onClick={() => alert("vai para trecho")}
                                                />
                                            );
                                        })}
                                    </Grid>
                                    <Grid item lg={1} xs={1}>
                                        <Button
                                            className={classes.addLeg}
                                            color="primary"
                                            icon={<Plus />}
                                            size="small"
                                            addRemove
                                            variant="outlined"
                                            iconPosition="center"
                                            onClick={() => alert("adiciona trecho")}
                                        />
                                    </Grid>
                                </Grid>
                            </Grid>
                        )}
                        <Grid
                            item
                            lg={4}
                            xs={12}
                            className={`${classes.grid} ${classes.origin}`}
                        >
                            <Grid container>
                                <Grid item xs={6} className={classes.originField}>
                                    <PesqincAir
                                        value={origin}
                                        iata={(originItem && originItem.IATA) || "Origem"}
                                        search={search}
                                        groupedDestinations={groupedDestinations}
                                        onChange={e => handlePesqinc(e, "origin")}
                                        onSelect={item => handleSelect(item, "origin")}
                                        onClose={() => handleClose("origin")}
                                        isMobile={props.isMobile}
                                        history={historySearch}
                                        mostSearched={mostSearched}
                                        searchOrigin
                                        transparent
                                        resetValue={e => handleReset(e, "origin")}
                                    />
                                </Grid>
                                <span className={classes.icon}>
                                    <Air size="medium" />
                                </span>
                                <Grid item xs={6} className={classes.destinyField}>
                                    <PesqincAir
                                        value={destiny}
                                        iata={(destinyItem && destinyItem.IATA) || "Destino"}
                                        search={search}
                                        groupedDestinations={groupedDestinations}
                                        onChange={item => handlePesqinc(item, "destiny")}
                                        onSelect={item => handleSelect(item, "destiny")}
                                        isMobile={props.isMobile}
                                        history={historySearch}
                                        mostSearched={mostSearched}
                                        searchOrigin={false}
                                        resetValue={e => handleReset(e, "destiny")}
                                    />
                                </Grid>
                            </Grid>
                        </Grid>
                        <Grid item lg={3} xs={12} className={classes.grid}>
                            <FullCalendar
                                onDatesChange={dates => {}}
                                isMobile={props.isMobile}
                                error={false}
                                errorMsg={null}
                                onOpen={handleOpen}
                                onClose={handleClose}
                            />
                        </Grid>
                        <Grid item lg={3} xs={12} className={classes.grid}>
                            <PaxAir
                                isMobile={false}
                                initialState={initialState}
                                open={openPax}
                                fields={fields}
                                onClick={handleOpenPax}
                                handleClose={handleClosePax}
                                handleAdd={handleAdd}
                                handleRemove={handleRemove}
                                handleClear={handleClear}
                                handleClasses={handleClasses}
                                handleDirectFlight={handleDirectFlight}
                                handleApply={handleApply}
                                error={false}
                                errorMsg=""
                            />
                        </Grid>
                        <Grid item lg={2} xs={12} className={classes.grid}>
                            <Button
                                color="secondary"
                                label="Buscar voos"
                                variant="contained"
                                size="large"
                                className={classes.btn}
                            />
                        </Grid>
                    </Grid>
                )}
                {!props.researchActive && props.research && (
                    <Grid container>
                        <Grid item className={classes.researchContentWrapper} xs={12}>
                            {props.destination.length === 1 &&
                props.destination.map((item, i) => (
                    <span key={i} className={classes.researchContent}>
                        {leg(item)}
                        <Typography
                            type="body2regular"
                            className={`${classes.text} ${classes.researchInfo}`}
                        >
                            <span className={classes.researchDate}>
                                <Calendar /> {item.departure} - {item.arrival}
                            </span>
                            <span className={classes.researchPax}>
                                <User /> {item.pax}
                            </span>
                        </Typography>
                    </span>
                ))}
                            {props.destination.length > 1 && (
                                <span className={classes.researchContent}>
                                    <span className={classes.researchMultidestination}>
                                        <Slider
                                            infinite={false}
                                            speed={500}
                                            slidesToShow={props.isMobile ? 1.4 : 2}
                                            touchThreshold={100}
                                            arrows={false}
                                            dots={false}
                                        >
                                            {props.destination.map((item, i) => leg(item, i))}
                                        </Slider>
                                    </span>
                                    <Typography
                                        type="body2regular"
                                        className={`${classes.text} ${classes.researchInfo}`}
                                    >
                                        <span className={classes.researchDate}>
                                            <Calendar /> {props.destination[0].departure} -{" "}
                                            {props.destination[props.destination.length - 1].arrival}
                                        </span>
                                        <span className={classes.researchPax}>
                                            <User /> {props.destination[0].pax}
                                        </span>
                                    </Typography>
                                </span>
                            )}
                            <Button
                                className={classes.researchBtn}
                                color="secondary"
                                label="Alterar busca"
                                variant="outlined"
                                size="small"
                            />
                        </Grid>
                    </Grid>
                )}
            </Container>
        </div>
    );
};

SearchAir.defaultProps = { isMobile: false };

SearchAir.propTypes = {
    classes: PropTypes.object.isRequired,
    research: PropTypes.bool,
    destination: PropTypes.array,
    isMobile: PropTypes.bool,
    researchActive: PropTypes.bool,
    type: PropTypes.string,
};

export default withStyles(styles, { withTheme: true })(SearchAir);
