import React from "react";
import { Menu, MenuItem } from "@material-ui/core";
import classNames from "classnames"
import { UserAreaIcon } from "."
import Button from "../../Button";
import Link from "../../Link";
import Typography from "../../Typography";
import { useStyles } from "../styles";

interface IUserAreaButtons {
    userName: string
    lightTheme: string
    className: string
    onClickLogout: () => void
    onClickMyTravels?: () => void
    employer?: boolean
    newLogin: boolean
    isCVC?: string
    isMobile?: boolean
}

const UserAreaButtons: React.FC<IUserAreaButtons> = ({
    userName,
    isMobile,
    lightTheme,
    className,
    onClickLogout,
    onClickMyTravels,
    employer,
    newLogin,
    isCVC
}: IUserAreaButtons) => {
    const classes = useStyles({});
    const [menuOpened, setMenuOpened] = React.useState(null)
    const menuButtonRef = React.useRef(null)

    const getOffset = (el) => {
        if (!el.current) return { top: 0, left: 0 };
        const rect = el.current.getBoundingClientRect();
        const menu:any = document.getElementById("customer-menu-icon")
        return {
            left: rect.left + window.scrollX - (menu.offsetWidth / 2.4),
            top: rect.top + window.scrollY - (el.current.offsetHeight / 2)
        };
    }

    const openPopup = (event) => {
        event.preventDefault()
        if (!employer) {
            setMenuOpened(event.currentTarget.parentNode)
        }
    }

    const closePopup = () => {
        setMenuOpened(null)
    }

    if (newLogin) {
        if (!isMobile) {
            return (
                <>
                    <div id="customer-menu-icon" className={classes.newButtonsContainer}>
                        {!employer
                            ? <Link className={classes.user} href="/mytravels/reservations" onClick={onClickMyTravels}>
                                <Typography type="body2semibold" className={classes.user}>
                                    Minhas Viagens
                                </Typography>
                            </Link>
                            : <Button onClick={onClickLogout} color="primary" label="Sair" size="small" variant="outlined" className={classNames(classes.logout, classes.logoutLeft)} />}
                        <a href="#" onClick={openPopup} ref={menuButtonRef}>
                            <UserAreaIcon rtl={!employer} text={null} />
                        </a>
                    </div>
                    <Menu
                        id="customer-menu"
                        // anchorEl={menuOpened}
                        anchorReference="anchorPosition"
                        anchorPosition={getOffset(menuButtonRef)}
                        keepMounted
                        open={Boolean(menuOpened)}
                        onClose={closePopup}
                        className={classes.menu}
                    >
                        <Typography type="body1semibold" className={classes.userInfo}>
                            Olá, {userName || employer ? "Colaborador(a)" : "Viajante"}!
                        </Typography>
                        {!employer && <MenuItem onClick={onClickMyTravels}><Typography type="body1regular">Minhas viagens</Typography></MenuItem>}
                        {!employer && isCVC && <MenuItem><Link className={classes.link} href="/mypoints"><Typography type="body1regular">Mais CVC: meus pontos</Typography></Link></MenuItem>}
                        <MenuItem onClick={onClickLogout}><Typography type="body1regular">Sair</Typography></MenuItem>
                    </Menu>
                </>
            )
        }
        return (
            <div className={classes.newButtonsContainerMobile}>
                <Typography type="headline5" className={classNames(classes.userInfo, {
                    [classes.userInfoMobile]: isMobile
                })}>
                    Olá, {userName || employer ? "Colaborador(a)" : "Viajante"}!
                </Typography>
                {!employer && <Link className={classes.linkMobile} href="/mytravels/reservations" onClick={onClickMyTravels}><Typography type="body1regular">Minhas viagens</Typography></Link>}
                {!employer && isCVC && (<Link href="/mypoints" className={classes.linkMobile}><Typography type="body1regular">Mais CVC: meus pontos</Typography></Link>)}
                <Link href="#" className={classes.linkLogout} onClick={onClickLogout}><Typography type="body1regular">Sair</Typography></Link>
            </div>
        )
    }

    return (
        <div className={`${lightTheme ? classes.loggedLight : classes.logged} ${className}`}>
            <Link className={classes.user} href="/mytravels/reservations" onClick={onClickMyTravels}>
                <Typography type="body2semibold" className={classes.user}>
                    Minhas Viagens
                </Typography>
            </Link>
            <Button onClick={onClickLogout} color="primary" label="Sair" size="small" variant="outlined" className={classes.logout} />
        </div>
    )
}

export default UserAreaButtons
