import React, { useState } from "react";
import { Paper, Divider, Typography, Grid } from "@material-ui/core";
import { makeStyles, MuiThemeProvider } from "@material-ui/core/styles";
import { storiesOf } from "@storybook/react";
import themes from "../../Theme/themes";
import PaxHotel from "../Hotel";
import { styles } from "../styles";

const useStyles = makeStyles((theme) => ({
    ...styles,
    rootBg: {
        background: theme.palette.primary.main,
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
}));

const PaxHotelStories = ({
    mode, brand, isMobile, error, errorMsg
}) => {
    const classes = useStyles(themes[brand]);
    const initialState = [{ value: null, adults: 1, children: 0, ages: [] }];
    const [open, setOpen] = useState(false);
    const [fields, setFields] = useState(initialState);
    const [valueTextField, setTextField] = useState({ value: "" });

    const handleOpen = () => {
        setOpen(!open);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleChange = (value) => {
        setTextField({ value });
        setOpen(false);
    };

    const handleAdd = () => {
        const values = [...fields];
        values.push(initialState[0]);
        setFields(values);
    };

    const handleRemove = (i) => {
        const values = [...fields];
        values.splice(i, 1);
        setFields(values);
    };

    const handleRemoveRoom = () => {
        const values = [...fields];
        values.pop();
        setFields(values);
    };

    const handleAdults = (idx, adult) => {
        const adults = [...fields];
        adults[idx].adults = adult;
        setFields(adults);
    };

    const handleAddChildren = (fieldIdx, child) => {
        const children = [...fields];
        children[fieldIdx].children = child;
        children[fieldIdx].ages.push({ value: 0 });
        setFields(children);
    };
    const handleRemoveChildren = (fieldIdx, child) => {
        const children = [...fields];
        children[fieldIdx].children = child;
        children[fieldIdx].ages.pop();
        setFields(children);
    };

    const handleChildAge = (fieldIdx, ageIdx, e) => {
        const childAge = [...fields];
        childAge[fieldIdx].ages[ageIdx].value = e.target.value;
        setFields(childAge);
    };
    const handleClear = () => {
        setFields(initialState);
        setTextField({ value: "" });
    };

    return (
        <MuiThemeProvider theme={themes[brand]}>
            <Paper className={classes[mode]} style={{ background: "#237FD0" }}>
                <Typography variant="h3">Pax Hotel</Typography>
                <br />
                <Divider />
                <br />
                <Grid container direction="row" justify="flex-start" spacing={2}>
                    <Grid item xs={12}>
                        <PaxHotel
                            className={classes}
                            isMobile={isMobile}
                            initialState={initialState}
                            open={open}
                            fields={fields}
                            onClick={handleOpen}
                            handleClose={handleClose}
                            handleAdd={handleAdd}
                            handleRemove={handleRemove}
                            handleRemoveRoom={handleRemoveRoom}
                            handleAdults={handleAdults}
                            handleAddChildren={handleAddChildren}
                            handleRemoveChildren={handleRemoveChildren}
                            handleChildAge={handleChildAge}
                            handleChange={handleChange}
                            valueTextField={valueTextField.value}
                            handleClear={handleClear}
                            error={error}
                            errorMsg={errorMsg}
                        />
                    </Grid>
                </Grid>
            </Paper>
        </MuiThemeProvider>
    );
};

const argsControls = {
    args: {
        brand: "Sub",
        mode: "rootBg",
        isMobile: false,
        error: false,
        errorMsg: "Error message"
    },
    argTypes: {
        brand: {
            type: "select",
            options: ["BlackAndWhite", "BlackFriday", "CVC", "CVCCorp", "CVCYellow", "Sub"]
        },
        mode: {
            type: "select",
            options: ["rootBg", "darkBg"]
        },
        isMobile: { type: "boolean" },
        error: { type: "boolean" },
        errorMsg: { type: "string" },
    }
}

storiesOf("Components/Pax/PaxHotel", module).addParameters({ storySource: { source: __STORY__, locationsMap: __LOCATIONS_MAP__ } })
    .add("Default", (args) => <PaxHotelStories {...args}/>, argsControls);
