import React from "react";
import Typography from "@material-ui/core/Typography";
import { jsxDecorator } from "storybook-addon-jsx";
import { Paper, Divider, Grid } from "@material-ui/core";
import { makeStyles, MuiThemeProvider } from "@material-ui/core/styles";
import { storiesOf } from "@storybook/react";
import { muiTheme } from "storybook-addon-material-ui";
import { Plus, Minus } from "@cvccorp-components/icons";
import Theme from "../../Theme";
import themes from "../../Theme/themes";
import Button from "../Button";
import { BrandType } from "../../types/brand";

const useStyles = makeStyles((theme) => ({
    root: {
        boxShadow: theme.shadows[24],
        padding: "30px",
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        padding: "20px",
    },
}));


type AddButtonsProps = {
    mode: string,
    size: string,
    brand: BrandType,
    color: string,
    variant: string,
    disabled: boolean
}

export const AddRemoveButtons = ({brand, mode, size, color, variant, disabled}: AddButtonsProps): JSX.Element => {
    const classes = useStyles(themes[brand]);
    return (
        <MuiThemeProvider theme={themes[brand]}>
            <Paper className={classes[mode]} id="botoes">
                <Typography variant="h3">Adicionar e Remover</Typography>
                <br />
                <Divider />
                <br />
                <Grid container direction="column" justify="center" spacing={2} alignItems="flex-start">
                    <Grid item xs={12}>
                        <Typography variant="subtitle1">Adicionar e Remover</Typography>
                        <br />
                        <Button
                            color={color}
                            icon={<Plus noGutter />}
                            size={size}
                            addRemove
                            variant={variant}
                            iconPosition="center"
                            disabled={disabled}
                        />
                        <Button
                            color={color}
                            icon={<Minus noGutter />}
                            size={size}
                            addRemove
                            variant={variant}
                            iconPosition="center"
                            disabled={disabled}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <Typography variant="subtitle1">Botão de vídeo</Typography>
                        <br />
                        <Button
                            color={color}
                            icon={<Plus noGutter />}
                            size={size}
                            play
                            iconPosition="center"
                            disabled={disabled}
                        />
                    </Grid>
                </Grid>
            </Paper>
        </MuiThemeProvider>
    );
};

const argsControls = {
    args: {
        brand: "CVC",
        mode: "root",
        size: 'medium',
        color: 'primary',
        variant: 'outlined',
        disabled: false
    },
    argTypes: {
        brand: {
            type: "select",
            options: ["BlackAndWhite", "BlackFriday", "CVC", "CVCCorp", "CVCYellow", "Sub"]
        },
        mode: {
            type: "select",
            options: ["root", "darkBg"]
        },
        size: {
            options: ['small', 'medium', 'large'],
            control: { type: 'select' }
        },
        color: {
            options: ['default', 'primary', 'secondary'],
            control: { type: 'select' }
        },
        variant: {
            options: ['outlined', 'contained'],
            control: { type: 'radio' }
        },
        disabled: { type: 'boolean' }
    }
}

storiesOf("Components/Buttons/Button", module).addParameters({ storySource: { source: __STORY__, locationsMap: __LOCATIONS_MAP__ } })
    .addDecorator(muiTheme([Theme]))
    .addDecorator(jsxDecorator)
    .add("Adicionar e Remover", (args) => <AddRemoveButtons {...args}/>, argsControls);
