import ArrowLeft from "@cvccorp-components/icons/dist/ArrowLeft";
import Calendar from "@cvccorp-components/icons/dist/Calendar";
import withStyles from "@material-ui/core/styles/withStyles";
import clsx from "clsx";
import moment from "moment";
import PropTypes from "prop-types";
import React, { useEffect, useState } from "react";
import Animate from "../../Animate";
import Button from "../../Button";
import Overlay from "../../Overlay";
import TextField from "../../TextField";
import Typography from "../../Typography";
import Calendar2days from "../Calendar2days";
import { styles } from "./styles";

const dateExtensiveFormat = date => moment(date, "YYYY-MM-DD").format("DD [de] MMM YYYY");

const FullCalendar = ({
    isOutsideRange,
    minDate,
    maxDate,
    classes,
    startDateInitial,
    endDateInitial,
    applyLabel,
    originLabel,
    destinyLabel,
    isMobile,
    onDatesChange,
    onOpen,
    onClose,
    isOpen,
    error,
    errorMsg,
    activeTextStart,
    activeTextEnd,
}) => {
    const [show, setShow] = useState(isOpen);
    const [calendarShow, setCalendarShow] = useState(isOpen);
    const [isActiveTextStart, setIsActiveTextStart] = useState(activeTextStart);
    const [isActiveTextEnd, setIsActiveTextEnd] = useState(activeTextEnd);
    const [inputFocus, setInputFocus] = useState("startDate");

    const [startDate, setStartDate] = useState(startDateInitial ? moment(startDateInitial) : null);
    const [endDate, setEndDate] = useState(endDateInitial ? moment(endDateInitial) : null);
    const [counterDays, setCounterDays] = useState(0);

    const [label1, setLabel1] = useState("");
    const [label2, setLabel2] = useState("");

    const [loaded, setLoaded] = useState(false);

    useEffect(() => {
        setLoaded(true);
        setLabel1(originLabel || "Data de ida");
        setLabel2(destinyLabel || "Data de volta");
    }, []);

    useEffect(() => {
        if (startDateInitial) setStartDate(moment(startDateInitial));
    }, [startDateInitial]);

    useEffect(() => {
        if (endDateInitial) setEndDate(moment(endDateInitial));
    }, [endDateInitial]);

    useEffect(() => {
        setIsActiveTextStart(activeTextStart);
    }, [activeTextStart]);

    useEffect(() => {
        setIsActiveTextEnd(activeTextEnd);
    }, [activeTextEnd]);

    useEffect(() => {
        if (startDate !== null && endDate !== null) {
            const counterDays = endDate?.diff(startDate, "days");
            setCounterDays(counterDays);
        }
    }, [startDate, endDate]);

    const setDates = dates => {
        switch (inputFocus) {
            case "startDate":
                setEndDate(null);
                setStartDate(dates.startDate);
                break;
            case "endDate":
                setEndDate(dates.endDate);
                break;
            default:
                break;
        }
        dates.endDate !== null && !isMobile ? setShow(true) : handleOpen();

        onDatesChange(dates);
    };

    const handleOpen = e => {
        setShow(true);
        setCalendarShow(true);
        setIsActiveTextStart(true);
        if (onOpen) onOpen(e);
        if (typeof window !== "undefined") {
            if (isMobile) document.body.classList.add("stop-scroll");
        }
    };

    const handleClose = e => {
        setShow(false);
        const closeCalendar = setTimeout(function() {
            setCalendarShow(false);
        }, 5000);
        clearTimeout(closeCalendar);
        if (onClose) onClose(e);
        if (typeof window !== "undefined") {
            if (isMobile) document.body.classList.remove("stop-scroll");
        }
    };

    const handleClear = () => {
        setStartDate(null);
        setEndDate(null);
        setCounterDays(0);
    };

    const apply = () => {
        if (endDate !== null && startDate !== null) handleClose();
    };

    const mountlabelButton = () => {
        if (isMobile) {
            if (counterDays > 0) {
                const daysWord = `diária${counterDays > 1 ? "s" : ""}`;
                return `Selecionar ${counterDays} ${daysWord} `;
            }
            return "Selecionar";
        } else {
            if (counterDays > 0) {
                const daysWord = `dia${counterDays > 1 ? "s" : ""}`;
                return `Viagem de ${counterDays} ${daysWord} `;
            }
            return "";
        }
    };

    const [isOpenAnimation, setOpenAnimation] = useState(false);

    const onOpenAnimate = () => {
        setOpenAnimation(true);
    };
    const onCloseAnimate = () => {
        setOpenAnimation(false);
    };

    return (
        <>
            {" "}
            {loaded && !isMobile ? (
                <div className={classes.containerDates}>
                <TextField
                    icon={<Calendar className={classes.calendarIcon}/>}
                    size="large"
                    disabled
                    label={"Data de ida"}
                    value={
                        startDate !== null ? `${startDate !== null && startDate._isValid ? dateExtensiveFormat(startDate) : label1}` : ""
                    }
                    onClick={() => handleOpen()}
                    error={error}
                    errorMsg={errorMsg}
                    inputClassName={clsx(classes.input,classes.inputLeft, classes.inputDate )}
                    labelClassName={clsx(classes.input,classes.inputDate)}
                    noBorder
                /> 
                <div className={classes.dateSeparator}/>
                <TextField
                    icon={<Calendar className={classes.calendarIcon}/>}
                    size="large"
                    disabled
                    label={"Data de volta"}
                    value={
                       endDate !== null ? `${endDate !== null && endDate._isValid ? dateExtensiveFormat(endDate) : label2}` : ""
                    }
                    onClick={() => handleOpen()}
                    error={error}
                    errorMsg={errorMsg}
                    inputClassName={clsx(classes.input, classes.inputRight, classes.inputDate)}
                    labelClassName={clsx(classes.input,classes.inputDate)}
                    noBorder
                />                
                </div>
            ) : ( 
            	<TextField
					icon={<Calendar className={classes.calendarIcon}/>}
					size="large"
                	disabled
                	label="Escolha as datas"
                	value={
                    	startDate !== null || endDate !== null ? `${startDate !== null && startDate._isValid ? dateExtensiveFormat(startDate) : label1} - ${endDate !== null && endDate._isValid ? dateExtensiveFormat(endDate) : label2}` : ""
                	}
                	onClick={() => handleOpen()}
                	error={error}
                	errorMsg={errorMsg}
                	inputClassName={clsx(classes.inputMobile, classes.inputRadius, classes.inputDate)}
                    labelClassName={clsx(classes.inputMobile,classes.inputDate)}
                	noBorder
            />
            )}{" "}
            <Animate isOpen={show} onOpen={onOpenAnimate} onClose={onCloseAnimate}>
                <Overlay onClick={() => handleClose()} />{" "}
                <div className={`${classes.calendar} ${!isOpenAnimation && classes.closed}`}>
                    <div className={classes.container}>
                        <div className={isMobile ? classes.headerMob : classes.header}>
                            <div className={isMobile ? classes.itensHeaderMob : classes.itensHeader}>
                                <Typography
                                    type="body1bold"
                                    className={classes.title}
                                    onClick={() => {
                                        isMobile && handleClose();
                                    }}
                                >
                                    {isMobile && (
                                        <>
                                            <ArrowLeft className={classes.icon} />
                                            Escolha as datas
                                        </>
                                    )}
                                </Typography>
                                <div className={classes.containerBtn}>{startDate && endDate && <Button className={classes.buttonClear} label="Limpar datas" onClick={() => handleClear()} />}</div>
                            </div>
                            <div className={isMobile ? classes.datesMob : classes.dates}>
                                <div className={classes.datesItem}>
                                    <TextField
                                        icon={<Calendar className={classes.calendarIcon}/>}
                                        size="large"
                                        placeholder={label1}
                                        error={error}
                                        errorMsg={errorMsg}
                                        inputClassName={clsx(classes.inputInside, classes.inputLeft, classes.inputDate)}
                                        labelClassName={classes.inputInside}
                                        rootClassName={clsx(classes.rootInput, classes.separator, isMobile ? classes.inputRadiusMobile : "")}
                                        value={startDate !== null && startDate._isValid ? dateExtensiveFormat(startDate) : ""}
                                        disabled
                                        noBorder
                                    />
                                </div>
                                <div className={classes.datesItem}>
                                    <TextField
                                        icon={<Calendar className={classes.calendarIcon}/>}
                                        size="large"
                                        placeholder={label2}
                                        error={error}
                                        errorMsg={errorMsg}
                                        inputClassName={clsx(classes.inputInside, classes.inputRight, classes.inputDate)}
                                        labelClassName={classes.inputInside}
                                        rootClassName={classes.rootInput}
                                        value={endDate !== null && endDate._isValid ? dateExtensiveFormat(endDate) : ""}
                                        disabled
                                        noBorder
                                    />
                                </div>
                            </div>
                        </div>
                        <div className={classes.content}>
                            <Calendar2days
                                minDate={minDate}
                                maxDate={maxDate}
                                startDate={startDate}
                                endDate={endDate}
                                onDatesChange={dates => setDates(dates)}
                                isMobile={isMobile}
                                focus={inputFocus}
                                isOutsideRange={isOutsideRange}
                                onFocusChange={e => {
                                    setInputFocus(e);
                                }}
                            />{" "}
                        </div>{" "}
                        {!isMobile && (
                            <div className={classes.footerContainer}>
                                <div className={classes.footerDayCounter}>
                                    <Typography type="body2semibold"> {mountlabelButton()}</Typography>
                                </div>
                                <div className={classes.footerConfirmButton}>
                                    <Button className={classes.btnApply} color="secondary" label="Selecionar" variant="contained" disabled={startDate === null || endDate === null} onClick={() => handleClose()} />
                                </div>
                            </div>
                        )}
                        {isMobile && (
                            <div className={`${classes.footer} ${show && classes.showFooter}`}>
                                <Button color="secondary" variant="contained" fullWidth label={applyLabel || mountlabelButton()} onClick={() => apply()} />
                            </div>
                        )}{" "}
                    </div>{" "}
                </div>{" "}
            </Animate>{" "}
        </>
    );
};

FullCalendar.propTypes = {
    classes: PropTypes.object.isRequired,
    onDatesChange: PropTypes.func.isRequired,
    isOutsideRange: PropTypes.number
};

export default withStyles(styles, {
    withTheme: true
})(FullCalendar);
