const styles = (theme) => ({
    root: {
        padding: `${theme.spacing(4)}px 0`,
        [theme.breakpoints.between("md", "xl")]: {
            padding: `${theme.spacing(8.875)}px ${theme.spacing(6)}px`,
        },
    },
    main: {
        [theme.breakpoints.between("md", "xl")]: {
            marginBottom: theme.spacing(6),
        },
    },
    mainItems: {
        [theme.breakpoints.between("xs", "md")]: {
            paddingTop: "0 !important",
        },
    },
    logoContainer: {
        textAlign: "right",
        [theme.breakpoints.between("xs", "md")]: { marginTop: "-14px" },
        "& img": {
            maxWidth: "70px",
            marginTop: "-16px",
            [theme.breakpoints.between("xs", "md")]: { maxWidth: "40px" },
        },
    },
    links: {
        color: theme.palette.text.primary,
        display: "block",
        textDecoration: "none",
        "& + &": { marginTop: theme.spacing(1.5) },
    },
    title: {
        margin: "0 0 24px",
    },
    iconContainer: { display: "flex" },
    icon: {
        alignItems: "center",
        background: theme.palette.text.disabled,
        borderRadius: "100%",
        color: theme.palette.text.primary,
        display: "flex",
        justifyContent: "center",
        height: "40px",
        width: "40px",
        "& + &": { marginLeft: theme.spacing(2) },
    },
    paymentGrid: {
        borderTop: `1px solid ${theme.palette.text.disabled}`,
        marginTop: theme.spacing(5),
        paddingTop: theme.spacing(5),
        [theme.breakpoints.between("xs", "md")]: { display: "none" },
    },
    footer: {
        "&:before": {
            borderTop: `2px solid ${theme.palette.secondary.main}`,
            content: "\"\"",
            display: "block",
            margin: `${theme.spacing(5)}px 0 ${theme.spacing(3)}px`,
            width: "40px",
        },
    },
    footerText: {
        color: theme.palette.text.secondary,
        // display: 'block',
        "& + &": {
            marginLeft: "3px",
            "&:before": {
                content: "-",
                marginRight: "3px",
            },
        },
    },
    breakMobile: {
        [theme.breakpoints.between("xs", "md")]: { display: "block" },
    },
    list: { padding: 0 },
    listItem: {
        display: "inline-block",
        listStyle: "none",
        "& + &": { marginLeft: theme.spacing(2) },
    },
});
export default styles;
