import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";

import Help from "@cvccorp-components/icons/dist/Help";
import Sac from "@cvccorp-components/icons/dist/Sac";
import DropRight from "@cvccorp-components/icons/dist/DropRight";
import Dropdown from "@cvccorp-components/icons/dist/DropDown";
import Typography from "../Typography";
import UserArea from "../UserArea";
import DropDown from "../DropDown";
import SubmenuMobile from "../SubmenuMobile";
import Animate from "../Animate";

import Logo from "../Logo";
import { styles } from "./styles";

const MenuMobile = ({ ctx, classes, product, menu, more, user, submenu, phone, phoneSales, help, goBack, isOpen }) => {
    const getMenu = () => {
        const menus = [];
        menu.forEach(item => {
            menus.push(
                <a href={item.link} target={item.target} className={classes.dropdownLink}>
                    <Typography type="body1regular">
                        {item.icon} {item.label}
                    </Typography>
                </a>
            );
        });
        return menus;
    };
    const [isMore, setMore] = useState(false);
    const [isOpenAnimation, setOpenAnimation] = useState(isOpen);

    const onOpen = () => {
        setOpenAnimation(true);
    };
    const onClose = () => {
        setOpenAnimation(false);
    };

    return (
        <Animate isOpen={isOpen} onOpen={onOpen} onClose={onClose}>
            {isOpenAnimation && <span className={classes.overlay} />}
            <div className={`${isOpenAnimation && classes.opened}  ${classes.root}`}>
                <header
                    className={classes.header}
                    onClick={() => {
                        goBack(true);
                    }}
                >
                    <Logo type="dark" className={classes.logo} />
                    <Typography type="captionregular">
                        {product} <Dropdown />
                    </Typography>
                </header>
                <UserArea lightTheme className={classes.userArea} user={user} />

                {!ctx.newLoginButton ? (
                    <div className={classes.menuHighlight}>
                        <a className={classes.menuHighlightItem} href={`tel:${phone}`}>
                            <Typography type="body2regular">
                                <Sac /><br /> Atendimento
                            </Typography>
                        </a>
                        {phoneSales &&
                            <a className={classes.menuHighlightItem} href={`tel:${phoneSales}`}>
                                <Typography type="body2regular">
                                    Televendas
                                </Typography>
                            </a>
                        }
                        <a className={classes.menuHighlightItem} href={help}>
                            <Typography type="body2regular">
                                <Help /><br /> Ajuda
                            </Typography>
                        </a>
                    </div>
                ) : (
                    <div className={classes.menuList}>
                        <Typography type="headline5">
                            Atendimento ao cliente
                        </Typography>
                        <a className={classes.menuListItem} href={`tel:${phone}`}>
                            <Typography type="body1regular">
                                Fale com a gente
                            </Typography>
                        </a>
                        {phoneSales &&
                            <a className={classes.menuListItem} href={`tel:${phoneSales}`}>
                                <Typography type="body1regular">
                                    Televendas
                                </Typography>
                            </a>
                        }
                        <a className={classes.menuListItem} href={help}>
                            <Typography type="body1regular">
                                Central de ajuda: Dúvidas frequentes
                            </Typography>
                        </a>
                    </div>
                )}

                <DropDown className={classes.dropdown} menu={getMenu()} />

                {more && (
                    <>
                        <a
                            onClick={() => {
                                setMore(true);
                            }}
                            className={classes.more}
                            href="#"
                        >
                            <Typography type="headline5">Mais</Typography>
                            <DropRight />
                        </a>
                    </>
                )}
            </div>
            <SubmenuMobile
                clickBack={() => {
                    setMore(false);
                }}
                isOpen={isMore}
                menu={submenu}
            />
        </Animate>
    );
};

MenuMobile.propTypes = {
    product: PropTypes.string,
    classes: PropTypes.oneOfType([PropTypes.object, PropTypes.string]),
    isOpen: PropTypes.bool,
    goBack: PropTypes.func,
    menu: PropTypes.array,
    more: PropTypes.bool,
    user: PropTypes.array,
    submenu: PropTypes.array,
    ctx: PropTypes.object
};

export default withStyles(styles, { withTheme: true })(MenuMobile);
