export const styles = theme => ({
    root: {
        boxShadow: theme.shadows[0],
        padding: 0,
    },
    legContainer: {
        background: theme.palette.white.main,
        borderRadius: "16px",
        boxShadow: theme.shadows[16],
        overflow: "hidden",
        margin: 0,
        padding: theme.spacing(2),
    },
    leg: {
        listStyle: "none",
        "& + &": {
            borderTop: `1px solid ${theme.palette.grey[50]}`,
            marginTop: theme.spacing(2),
            paddingTop: theme.spacing(2),
        },
    },
    header: {
        display: "flex",
        marginBottom: theme.spacing(2),
    },
    cia: { textTransform: "capitalize" },
    ciaLogo: {
        marginRight: theme.spacing(2),
        maxHeight: "32px",
        maxWidth: "32px",
    },
    date: { display: "block" },
    price: {
        background: theme.palette.white.main,
        borderRadius: "16px",
        boxShadow: theme.shadows[16],
        overflowX: "hidden",
        padding: theme.spacing(2),
        position: "relative",
        "&:before": {
            borderTop: `2px dashed ${theme.palette.grey[50]}`,
            content: "\"\"",
            display: "block",
            left: 0,
            position: "absolute",
            top: 0,
            width: "100%",
            [theme.breakpoints.between("md", "xl")]: {
                borderTop: "none",
                borderLeft: `2px dashed ${theme.palette.grey[50]}`,
                bottom: 0,
                width: 0,
            },
        },
        [theme.breakpoints.between("md", "xl")]: {
            padding: theme.spacing(3),
        },
    },
    priceLabel: { display: "block" },
    detailBtn: {
        color: theme.palette.primary.main,
        display: "block",
        height: "auto",
        padding: 0,
        [theme.breakpoints.between("md", "xl")]: {
            marginBottom: theme.spacing(2),
        },
    },
    priceTotalAir: {
        color: theme.palette.primary.main,
        marginLeft: "4px",
    },
    priceTotalPackage: {
        color: "#FF5D00",
        marginLeft: "4px",
    },
    luggage: {
        color: theme.palette.text.primary,
    },
    centerContent: {
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center",
    },
    stops: { padding: `0 ${theme.spacing(2)}px` },
    stopsIcon: { color: theme.palette.text.primary },
    airport: { display: "flex" },
    legFooter: {
        display: "flex",
        justifyContent: "space-between",
        [theme.breakpoints.between("md", "xl")]: {
            flexDirection: "row-reverse",
        },
    },
    spacing: {
        justifyContent: "space-between",
    },
    title: {
        padding: "0 0 25px 5px",
    },
    paddingTop24: {
        paddingTop: theme.spacing(3),
    },
    btnChangeFlight: {
    // marginTop: "20px",
        [theme.breakpoints.between("xs", "sm")]: {
            marginTop: "20px",
            width: "100%",
        },
    },
    noWrap: {
        flexWrap: "nowrap",
        alignItems: "center",
        paddingBottom: theme.spacing(2),
        paddingTop: theme.spacing(2),
    },
    radio: {
        "&$checked": {
            color: theme.palette.primary.main,
        },
    },
    checked: {},
    orangeColor: {
        color: theme.palette.package.main,
    },
    tooltip: {
        "& span.tooltip": { top: "-44px !important", left: "17%" },
    },
    modal: {
        position: "fixed",
        zIndex: 99,
        top: 0,
        bottom: 0,
        left: 0,
        right: 0,
        width: "100%",
    },
    scroll: {
        overflow: "scroll",
        height: "100%",
        width: "100%",
        backgroundColor: "#fff",
        display: "flex",
        alignItems: "flex-start",
        flexDirection: "column",
    },
    close: {
        display: "flex",
        flexDirection: "row",
        alignItems: "center",
        borderBottom: "1px solid #ddd",
        padding: "10px",
        width: "100%",
    },
});
