import {
  useEffect
} from "react";

const GoogleAd = ({
  dfps,
  isMobile
}) => {
  useEffect(() => {
    if (window) {
      const googletag = window.googletag || {};

      googletag.cmd = googletag.cmd || [];

      googletag.cmd.push(() => {
        dfps.forEach(({
          image,
          dimensions,
          tag
        }) => {
          const dimension = !isMobile ? 0 : 1;

          googletag
            .defineSlot(image, dimensions[dimension], tag)
            .addService(googletag.pubads());
        });
        googletag.pubads().enableSingleRequest();
        googletag.pubads().enableSyncRendering();
        googletag.enableServices();
        googletag.display(...dfps.map(({
          tag
        }) => tag));
      });
    }
  });
};

export default GoogleAd;
