export const styles = theme => ({
    root: {
        borderRadius: "16px",
        boxShadow: theme.shadows[8],
        cursor: "pointer",
        padding: theme.spacing(2),
        position: "relative",
        "&:hover": {
            boxShadow: theme.shadows[16],
            "& $img": {
                transform: "translate(-50%, -50%) scale(1.07)"
            }
        },
        [theme.breakpoints.between("md", "xl")]: {
            "&:before": {
                border: "2px solid transparent",
                borderRadius: "16px",
                "-webkit-border-radius" :`${theme.spacing(2)}px`,
                bottom: 0,
                content: "''",
                left: 0,
                position: "absolute",
                right: 0,
                top: 0,
                zIndex: theme.level.content,
                transition: "border-color .3s"
            },
            "&:hover:before": {
                borderColor: theme.palette.spot.main
            }
        },
    },
    hotel: { "&:hover:before": { borderColor: theme.palette.hotel.main } },
    air: { "&:hover:before": { borderColor: theme.palette.air.main }, "&:focus-visible:before": {borderColor: theme.palette.air.main} },
    package: { "&:hover:before": { borderColor: theme.palette.package.main } },
    car: { "&:hover:before": { borderColor: theme.palette.car.main } },
    img: {
        display: "block",
        left: "50%",
        position: "absolute",
        transform: "translate(-50%, -50%)",
        transition: "transform .2s",
        top: "50%",
    },
    title: {
        display: "block",
        height: "28px",
        marginTop: theme.spacing(3),
        overflow: "hidden",
        textOverflow: "ellipsis",
        whiteSpace: "nowrap",
    },
    footer: {
        borderTop: `1px solid ${theme.palette.grey[50]}`,
        margin: `${theme.spacing(2)}px ${theme.spacing(2) * -1}px ${theme.spacing(2) * -1}px `,
        padding: `${theme.spacing(2)}px 0`,
        textAlign: "center",
    },
    footerItem: {
        "& + &": {
            marginLeft: theme.spacing(1) / 2,
            "&:before": {
                borderLeft: `1px solid ${theme.palette.text.primary}`,
                content: "\"\"",
                display: "inline-block",
                height: "10px",
                marginRight: theme.spacing(1) / 2,
                verticalAlign: "middle",
            },
        },
    },
    header: {
        height: "310px",
        // minHeight: '125px',
        // marginBottom: theme.spacing(4),
    },
    headerSmall: { height: "270px" },
    headerNoImg: { height: "175px" },
    priceContainer: {
        // marginTop: theme.spacing(4),
    },
    priceLabel: {
        display: "block",
        marginBottom: theme.spacing(1) / 2,
    },
    imgContainer: {
        backgroundClip: "padding-box",
        borderRadius: "16px 16px 0 0",
        "-webkit-border-radius" :"16px 16px 0 0",
        display: "block",
        position: "relative",
        marginLeft: theme.spacing(2) * -1,
        marginTop: theme.spacing(2) * -1,
        width: `calc(100% + ${theme.spacing(4)}px)`,
        "-webkit-mask-image": "-webkit-radial-gradient(white, black)",
    },
    imgContainerPromo: {
        background: theme.palette.backdrop.main,
        display: "block",
        height: "185px",
        overflow: "hidden",
        position: "relative",
    },
    imgContainerPromoSmall: {
        background: theme.palette.backdrop.main,
        display: "block",
        height: "135px",
        overflow: "hidden",
        position: "relative",
    },
    tagContainer: { position: "relative" },
    tag: {
        bottom: "-12px",
        position: "absolute",
        right: theme.spacing(0.625),
    },
    subtitle: {
        display: "block",
        color: theme.palette.text.secondary,
        height: "24px",
        overflow: "hidden",
        textOverflow: "ellipsis",
        whiteSpace: "nowrap",
    },
    date: {
        display: "block",
        overflow: "hidden",
        textOverflow: "ellipsis",
        whiteSpace: "nowrap",
    },
    departure: { display: "block" },
    arrival: { display: "block" },
    totalDays: { marginRight: theme.spacing(1) / 2 },
    loadingContent: { marginTop: theme.spacing(2) },
    loadingPrice: { marginTop: theme.spacing(5) },
    stars: {
        color: theme.palette.secondary.main,
        // marginRight: theme.spacing(0.5),
    },
    break: { display: "block" },
    icon: { marginRight: theme.spacing(1) / 2 },
    item: {
        display: "flex",
        alignItems: "center",
    },
    items: {
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        flexWrap: "wrap",
    },
    itemIcon: {
        display: "flex",
        backgroundColor: theme.palette.grey[200],
        borderRadius: "50%",
        padding: theme.spacing(1),
        alignItems: "center",
        justifyContent: "center",
        marginRight: theme.spacing(1) / 2,
        "& svg": {
            width: theme.spacing(2),
            height: theme.spacing(2),
        }
    },
    smallItemIcon: {
        display: "flex",
        backgroundColor: theme.palette.grey[200],
        borderRadius: "50%",
        padding: theme.spacing(1),
        alignItems: "center",
        justifyContent: "center",
        marginRight: theme.spacing(1) / 2,
        "& svg": {
            width: theme.spacing(2) / 1.5,
            height: theme.spacing(2) / 1.5,
        }
    }
});
