import React, { useState } from "react";
import {
    Wifi,
    Parking,
    Bar,
    AirConditioner,
    Reception24hs,
    Pool,
    Kids,
    Restaurant,
    Gym,
} from "@cvccorp-components/icons";
import { Paper, Divider, } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import { makeStyles , MuiThemeProvider } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import { storiesOf } from "@storybook/react";
import themes from "../../Theme/themes";
import { BrandType } from "../../types/brand";
import FilterGrid from "../FilterGrid";

const useStyles = makeStyles((theme) => ({
    root: {
        background: theme.palette.background.default,
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
}));
const initialItems = [
    {
        code: 1,
        label: "Academia",
        icon: Gym,
        size: "medium",
        checked: false
    },
    {
        code: 2,
        label: "Restaurante",
        icon: Restaurant,
        size: "medium",
        checked: false
    },
    {
        code: 3,
        label: "Ideal para crianças",
        icon: Kids,
        size: "medium",
        checked: false
    },
    {
        code: 4,
        label: "Piscina",
        icon: Pool,
        size: "medium",
        checked: false
    },
    {
        code: 5,
        label: "Recepção 24hs",
        icon: Reception24hs,
        size: "medium",
        checked: false
    },
    {
        code: 6,
        label: "Ar condicionado",
        icon: AirConditioner,
        size: "medium",
        checked: false
    },
    {
        code: 7,
        label: "Estacionamento",
        icon: Parking,
        size: "medium",
        checked: false
    },
    {
        code: 8,
        label: "Wi-fi grátis",
        icon: Wifi,
        size: "medium",
        checked: false
    },
    {
        code: 9,
        label: "Bar",
        icon: Bar,
        size: "medium",
        checked: false
    }
];

type FilterGridStoryProps = {
    mode: string,
    brand: BrandType,
    label: string,
    isOpen: boolean,
    isMobile: boolean,
    isAmenities: boolean,
}

const FilterGridStory = ({
    mode, brand, label, isOpen, isMobile, isAmenities
}: FilterGridStoryProps) => {
    const [items, setItems] = useState(initialItems);
    const classes = useStyles(themes[brand]);

    return (
        <MuiThemeProvider theme={themes[brand]}>
            <Paper className={classes[mode]} id="FilterGrid">
                <Typography variant="h3">FilterGrid</Typography>
                <br />
                <Divider />
                <br />
                <Grid item xs={4}>
                    <FilterGrid
                        label={label}
                        isMobile={isMobile}
                        isAmenities={isAmenities}
                        items={items}
                        open={isOpen}
                        onChange={(itemChange, itens) => {
                            setItems([...itens])
                        }}
                        onReset={() => setItems(initialItems)}
                    />
                </Grid>
                <div style={{padding: 150}}> </div>
            </Paper>
        </MuiThemeProvider>

    );
};

const argsControls = {
    args: {
        brand: "CVC",
        mode: "root",
        label: "Principais Comodidades",
        isOpen: false,
        isMobile: false,
        disabled: false,
    },
    argTypes: {
        brand: {
            type: "select",
            options: ["BlackAndWhite", "BlackFriday", "CVC", "CVCCorp", "CVCYellow", "Sub"]
        },
        mode: {
            type: "select",
            options: ["root", "darkBg"]
        },
        label: { type: "string" },
        isOpen: { type: "boolean" },
        isMobile: { type: "boolean" },
        isAmenities: { type: "boolean" }
    }
}

storiesOf("Components/Filtros/FilterGrid", module).addParameters({ storySource: { source: __STORY__, locationsMap: __LOCATIONS_MAP__ } })
    .add("Default", (args) => <FilterGridStory {...args}/>, argsControls);
