import styled from "styled-components";

export const Bubbly = styled.button`
  // background: ${props => (props.active === true ? "#97007C" : "#fff")};
  background: none;
  // border-radius: 30px;
  border: none;
  // box-shadow: 0px 0px 8px 0px rgba(0,0,0,0.16);
  color: ${props => (props.active === true ? "#fff" : "#97007C")};
  cursor: pointer;
  font-size: 12px;
  font-family: HindMRegular;
  line-height: 1.3;
  margin: 0 auto 40px;
  max-width: 400px;
  outline: none;
  padding: 20px 16px;
  position: relative;
  width: 100%;
  white-space: nowrap;

  &:hover {
    color: #fff;
    font-weight: 700;

    &:after {
      background: #97007C;
    }

    &:before {
      border-top-color: #97007C;
    }
  }

  &:focus {
    color: #fff;
    font-weight: 700;

    &:after {
      background: #97007C;
    }

    &:before {
      border-top-color: #97007C;
    }  
  }

  &:after {
    background: ${props => (props.active === true ? "#97007C" : "#fff")};
    border-radius: 16px;
    bottom: 8px;
    box-shadow: 0px 0px 8px 0px rgba(0,0,0,0.16);
    content: "";
    left: 8px;
    position: absolute;
    right: 8px;
    top: 8px;
    z-index: -1;
  }

  &:before {
    border: 10px solid transparent;
    border-top-color: ${props => (props.active === true ? "#97007C" : "#fff")};
    bottom: -10px;
    content: "";
    height: 0;
    left: 50%;
    position: absolute;
    transform: translateX(-50%);
    width: 0;
  }
`;
