export const styles = theme => ({
    formControl: {
        borderRadius: "8px",
        boxSizing: "border-box",
        padding: `${theme.spacing(1)}px 0`,
        position: "relative",
        width: "100%"
    },
    root: {
        borderColor: theme.palette.grey[50],
        fontSize: "14px",
        padding: theme.spacing(1),
        paddingLeft: theme.spacing(2)
    },
    large: {
        borderColor: theme.palette.grey[50],
        boxSizing: "border-box",
        fontSize: "14px",
        height: "56px",
        padding: `${theme.spacing(2.5)}px ${theme.spacing(5)}px ${theme.spacing(2)}px ${theme.spacing(2)}px`
    },
    disabled: {
        background: theme.palette.text.disabled,
        borderColor: theme.palette.text.disabled,
        color: theme.palette.text.secondary
    },
    label: {
        color: `${theme.palette.text.primary} `,
        fontSize: "14px",
        transform: "translate(14px, 12px) scale(1)"
    },
    labelLarge: {
        color: `${theme.palette.text.primary} `,
        fontSize: "14px",
        transform: "translate(14px, 20px) scale(1)"
    },
    labelSearchOrigin: { transform: "translate(14px, 32px) scale(1)" },
    labelSearchDestiny: {
        transform: "translate(140%, 32px) scale(1)",
        [theme.breakpoints.between("xs", "sm")]: {
            transform: "translate(120%, 32px) scale(1)"
        }
    },
    lbIcon: { transform: "translate(40px, 10px) scale(1) " },
    lbIconLarge: { transform: "translate(50px, 20px) scale(1)" },
    lbIconLargeNoBorder: { transform: "translate(60px, 20px) scale(1)" },
    disabledLabel: { color: `${theme.palette.text.secondary} ` },
    errorText: {
        color: `${theme.palette.error.main} `,
        fontFamily: theme.font.semibold,
        fontSize: "12px "
    },
    icon: {
        left: "10px",
        position: "absolute",
        top: "50%",
        transform: "translateY(-50%)",
        zIndex: 2
    },
    iconError: { marginTop: "-10px" },
    iconNoBorder: {
        paddingRight: theme.spacing(1),
        "&:after": {
            borderRight: `1px solid ${theme.palette.grey[50]}`,
            bottom: 0,
            content: "\"\"",
            position: "absolute",
            right: 0,
            top: 0
        }
    },
    selectIcon: { paddingLeft: "40px" },
    inputIcon: { textIndent: "30px" },
    inputIconLarge: { textIndent: "50px" },
    noBorder: {
        boxShadow: theme.shadows[16],
        borderRadius: "8px",
        border: "none"
    },
    transparent: {
        background: "transparent",
        color: theme.palette.white.main,
        "& fieldset": { border: "none" }
    },
    transparentRoot: {
        "& fieldset": { border: "none" },
        "& .MuiInputBase-root": {
            background: `${theme.palette.primary.alphaDark} !important`,
            borderRadius: "8px"
        },
        "& .MuiInputBase-root > .MuiSvgIcon-root": {
            fill: `${theme.palette.primary.contrastText} !important`,
            color: `${theme.palette.primary.contrastText} !important`
        }
    },
    noBorderTransparent: {
        boxShadow: "unset"
    },
    lbtransparent: { color: `${theme.palette.white.main} !important` },
    specialOutline: { border: "none" },
    flexContent: {
        display: "flex"
    }
});
// .MuiInputBase-root
