import React from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Grid from "@material-ui/core/Grid";
import Meal from "@cvccorp-components/icons/dist/Meal";
import Typography from "../../Typography";
import Button from "../../Button";
import Tag from "../../Tag";
import Box from "../../Box";
import Price from "../Price";
import Info from "../Info";
import Amenities from "../Amenities";
import { styles } from "./styles";

const Card = ({
    classes,
    onClick,
    thumb,
    title,
    onDetail,
    meal,
    bedInfo,
    noRefundable,
    amenities,
    product,
    totalPrice,
    partialPrice,
    pax,
    days,
    taxesIncluded,
    parcel,
}) => {
    return (
        <Box className={classes.root} onClick={onClick}>
            <Grid container spacing={2} alignItems="center">
                <Grid item xs={2}>
                    <div className={classes.imgContainer}>
                        <img className={classes.thumb} src={thumb} alt="" />
                    </div>
                </Grid>
                <Grid item xs={5}>
                    <h1 className={classes.title}>
                        <Typography type="body2semibold">{title}</Typography>
                    </h1>
                    {onDetail && (
                        <Button
                            className={classes.detailBtn}
                            color="primary"
                            outline
                            label="Ver detalhes"
                            variant="text"
                            onClick={onDetail}
                        />
                    )}
                </Grid>
                {meal && (
                    <Grid item xs={5} className={classes.detail}>
                        <Tag label={meal} icon={<Meal />} />
                    </Grid>
                )}
            </Grid>
            <Info
                className={classes.infoContainer}
                bedInfo={bedInfo}
                noRefundable={noRefundable}
            />
            {amenities && <Amenities amenities={amenities} />}
            <Price
                onClick={onClick}
                product={product}
                totalPrice={totalPrice}
                partialPrice={partialPrice}
                totalPeople={pax}
                days={days}
                taxesIncluded={taxesIncluded}
                parcel={parcel}
            />
        </Box>
    );
};

Card.propTypes = {
    classes: PropTypes.object.isRequired,
    onClick: PropTypes.func.isRequired,
    thumb: PropTypes.string,
    title: PropTypes.string.isRequired,
    meal: PropTypes.bool,
    product: PropTypes.string.isRequired,
    pax: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
    days: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
    totalPrice: PropTypes.oneOfType([PropTypes.number, PropTypes.string])
        .isRequired,
    partialPrice: PropTypes.oneOfType([PropTypes.number, PropTypes.string])
        .isRequired,
    taxesIncluded: PropTypes.bool,
    parcel: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
    onDetail: PropTypes.func.isRequired,
    noRefundable: PropTypes.bool,
    totalDays: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
    bedInfo: PropTypes.string,
    amenities: PropTypes.string.array,
};

export default withStyles(styles, { withTheme: true })(Card);
